css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweakers.net") {
    /*[[showcaseitems]]*/

    #ticker a:hover {
        text-decoration:underline!important;
    }
    
    #about .button:hover {
        background:linear-gradient(#6B6B6F, #555858) repeat scroll 0 0 #6B6F6F!important;
    }


    #about .button.twitter span {
        background-position: 0 -16px!important;
    }

    
    #about .button.rss span {
        background-position: 0 -32px!important;
    }



    #entity {
        margin: 0 auto!important;
        width: 1020px!important;
    }

    #layout {
        padding-bottom:190px!important;
        background:#313232!important;
  
  		width: 1020px!important;
		margin: 0 auto!important;
		background: #FFF!important;
    }

    .mainColumn~div table.itemList {
        width:100%!important
    }

    .forumList:nth-child(3) table.listing th {
        background:#C91444!important;
        background:-webkit-linear-gradient(#C91444, #B1113A)!important;
        background:linear-gradient(#C91444, #B1113A)!important;
    }
    
    .forumList:nth-child(4) table.listing th {
        background:#6D5C7A!important;
        background:-webkit-linear-gradient(#6D5C7A, #5B4C68)!important;
        background:linear-gradient(#6D5C7A, #5B4C68)!important;
    }
    
    .forumList:nth-child(5) table.listing th {
        background:#68A62D!important;
        background:-webkit-linear-gradient(#68A62D, #5C9526)!important;
        background:linear-gradient(#68A62D, #5C9526)!important;
    }
    
    .forumList:nth-child(6) table.listing th {
        background:#E76718!important;
        background:-webkit-linear-gradient(#E76718, #CB530A)!important;
        background:linear-gradient(#E76718, #CB530A)!important;
    }
    
    .forumList:nth-child(7) table.listing th {
        background:#2EAE95!important;
        background:-webkit-linear-gradient(#2EAE95, #2EAE95)!important;
        background:linear-gradient(#2EAE95, #2EAE95)!important;
    }

    table.listing td.tags {
        font-size:9px!important;
        overflow:hidden;
        text-overflow:ellipsis;
        white-space:nowrap;
    }

    .articleColumn, .reactie {
        line-height:1.5!important;
    }

    .frontPageLink:visited {
        color:#616464!important;
    }

    #bottom {
        background:#212323!important;
    }
    
    #jobs_relations, #social, #jobs, #newsletter, #highlights, #sponsored {
        display:none!important;
    }
    
    /*table.highlights td {
        padding:6px 0 !important;
    }

    table.highlights td.title {
        padding-left:6px!important;
    }
    
    table.listing td, table.listing th {
        padding:5px!important;
    }*/

	#top {
  		border-bottom:none;
	}

    #top, #bottom {
        min-width:1020px!important;
    }
    
    #contentArea, .takeoverBg #contentArea {
        padding-left:20px!important;
        padding-right:20px!important;
        background:#FFF;
        border-radius:0 0 6px 6px!important;
        margin-bottom:-6px!important;
        z-index:2;
        position:relative;
  	border-top: 1px solid #c3c6c6;
    }
    
    #menubar {
    }
    
    .popup.trackerPopup.fixed {
        box-shadow:none!important;
        top:0!important;
    }

    body {
        background: #313232!important;
        overflow:hidden!important;
    }
    
    #searchbar {
        border-radius:0;
    }
    
    #searchbar, .galleryHeadingContainer, .pageTabsContainer, #tweakbase, #tabs, .notificationsContainer {
        width:1020px;
        margin-left: auto;
        margin-right: auto;
    }
    
    #bottom div.hr {
        background-color: #FFFFFF;
        border-radius: 0 0 6px 6px;
        height: 6px;
        width:1020px;
        margin-left: auto;
        margin-right: auto;
        opacity:0;
    }

    
    /*#tracker{
        padding-top:137px!important;
        margin-bottom:167px!important;
    }
    
    #tracker .fakeTop > div {
        bottom:-41px!important;
    }
    
    .fakeTop {
        border-bottom:1px solid #C3C6C6!important;
        height:136px!important;
    }*/
    
    body {
        margin-right:0!important;
        margin-left:0!important;
    }
    
    body.trackerScroll.trackerVisible:not(.trackerRight) #tracker {
        left:0!important;
    }
    
    .trackerScroll.trackerRight #tracker {
        right:-6px!important;
    }
}
