css:
@-moz-document url-prefix("https://maps.google.com"), url-prefix("http://maps.google.com") {
/* Background */
body {
    background-image: url(/*[[backgound]]*/) !important;
    background-size: auto !important;
    background-position: center top !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background-color: transparent !important;
    color: rgba(255,255,255,0.9) !important;
}


/* Top Bar */
#gbx3 {
    border-color: rgba(255,255,255,0.9) !important;
    background-color: rgba(0, 0, 0, 0.5) !important;
}

.gbto .gbts {
    background-color: rgba(0, 0, 0, 0.5) !important;
    border-color: rgba(255,255,255,0.9) !important;
}

.gbm {
    border: 1px solid rgba(255,255,255,0.9) !important;
    background: rgba(0, 0, 0, 0.5) !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
}

.gbmh {
    border: 1px solid rgba(255,255,255,0.9) !important;
}

.gbto #gbs {
    background: none !important;
}

.gbmc {
    background: none !important;
}

#gbz .gbto .gbts, #gbd .gbmt {
    color: rgba(255,255,255,0.9) !important;
}

#gbz .gbz0l .gbts, #gbz .gbzt-hvr, #gbz .gbzt:focus, #gbz .gbgt-hvr, #gbz .gbgt:focus {
    color: rgba(255,255,255,0.9) !important;
}

#gbd .gbmt-hvr, #gbd .gbmt:focus {
    background-color: rgba(0, 0, 0, 0.5) !important;
    border-top: 1px dotted rgba(255,255,255,0.9) !important;
    border-bottom: 1px dotted rgba(255,255,255,0.9) !important;
}

#gbx1, #gbx2 {
    background: rgba(0, 0, 0, 0.5) !important;
    border-bottom: 1px solid rgba(255,255,255,0.9) !important;
    border-color: rgba(255,255,255,0.9) !important;
}

.gbma {
    border-top-color: rgba(255,255,255,0.9) !important;
}

.gbqfqw {
    background-color: rgba(0, 0, 0, 0.5) !important;
    border: 1px solid rgba(255,255,255,0.9) !important;
    border-top: 1px solid rgba(255,255,255,0.9) !important;
}

#gbqfq, #gbqfqb, #gbqfqc {
    color: rgba(255,255,255,0.9) !important;
}

.gssb_m {
    color: rgba(255,255,255,0.9) !important;
    background: rgba(0, 0, 0, 0.5) !important;
}

.gssb_e {
    border: 1px solid rgba(255,255,255,0.9) !important;
    border-top-color: rgba(255,255,255,0.9) !important;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
}

.gssb_i {
    background: rgba(0, 0, 0, 0.5) !important;
}

.bHa {
    color: rgba(255,255,255,0.9) !important;
}

.A1a {
    color: rgba(255,255,255,0.9) !important;
}

.oOa {
    background-color: rgba(0, 0, 0, 0.5) !important;
    border-bottom: 1px solid rgba(255,255,255,0.9) !important;
    border-top: 1px solid rgba(255,255,255,0.9) !important;
}

#gbg6 #gbi4t, #gbg4 #gbgs4d {
    color: rgba(255,255,255,0.9) !important;
}

#gbg1:active .gbid#gbi1a {
    border-color: rgba(255,255,255,0.9) !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
}

.gbgt-hvr .gbid#gbi1a, #gbg1:focus .gbid#gbi1a, #gbg1:active .gbid#gbi1a {
    background-color: rgba(0, 0, 0, 0.5) !important;
    background-image: none !important;
    border-color: rgba(255,255,255,0.9) !important;
}

.gbgt-hvr .gbid#gbi1a, #gbg1:focus .gbid#gbi1a, #gbg1:active .gbid#gbi1a {
    background-color: rgba(0, 0, 0, 0.5) !important;
    background-image: none !important;
    border-color: rgba(255,255,255,0.9) !important;
}

#gbg1:focus #gbi1a, #gbg1:active #gbi1a {
    background-color: rgba(0, 0, 0, 0.5) !important;
    background-image: none !important;
    border-color: rgba(255,255,255,0.9) !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
}

#gbg1:focus #gbi1a, #gbg1:active #gbi1a {
    background-color: rgba(0, 0, 0, 0.5) !important;
    background-image: none !important;
    border-color: rgba(255,255,255,0.9) !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
}

.gbqfb {
    background-color: rgba(0, 0, 0, 0.5) !important;
    background-image: none !important;
    border: 1px solid rgba(255,255,255,0.9) !important;
    color: rgba(255,255,255,0.9) !important;
}

.gbqfbb {
    background-color: rgba(0, 0, 0, 0.5) !important;
    background-image: none !important;
}

.onegoogle{
    -moz-transition: top .9s ease !important;
    -webkit-transition: top .9s ease !important;
    top: -91px !important;
}

.onegoogle:hover {
    top: 0px !important;
}

.Dob {
    height: 9px !important;
}

#page {
    top: -100px !important;
}




/* Left */
#topbar-startcol {
    border-bottom: 1px solid rgba(255,255,255,0.9) !important;
    background: rgba(0, 0, 0, 0.5) !important;
}

#panel {
    background-color: rgba(0, 0, 0, 0.5) !important;
}

#panelarrow2 {
    background: transparent !important;
}

.kd-button {
    border: 1px solid rgba(255,255,255,0.9) !important;
    color: rgba(255,255,255,0.9) !important;
    background-color: rgba(0, 0, 0, 0.5) !important;
    background-image: none !important;
}

.print {
    background-color: rgba(255,255,255,0.9) !important;
}

.link {
    background-color: rgba(255,255,255,0.9) !important;
}

a:visited {
    color: rgba(255,255,255,0.9) !important;
}

a {
    color: rgba(255,255,255,0.9) !important;
}

.mapsgl-promo-activate {
    background-color: rgba(0, 0, 0, 0.5) !important;
    border: 1px solid rgba(255,255,255,0.9) !important;
}

.promotitle {
    color: rgba(255,255,255,0.9) !important;
}

.mapsgl-promo-activate #mapsgl-agree {
    color: rgba(255,255,255,0.9) !important;
}

.rmi-rpl-k {
    background-color: rgba(0, 0, 0, 0.5) !important;
}
}
