/* Domain'e göre AdBlock */
*[src*="ad.donanimhaber.com"],
*[href*="ad.donanimhaber.com"],
*[data*="ad.donanimhaber.com"],
*[src*="adserve.donanimhaber.com"],
*[href*="adserve.donanimhaber.com"],
*[data*="adserve.donanimhaber.com"],
*[src*="reklam.donanimhaber.com"],
*[href*="reklam.donanimhaber.com"],
*[data*="reklam.donanimhaber.com"],
*[src*="img.donanimhaber.com/ads/"],
*[src*="static.donanimhaber.com/reklam/"],
*[src*="serving-sys.com"],
*[href*="serving-sys.com"],
*[data*="serving-sys.com"],

/* Element'e göre AdBlock */
iframe, /* Reklamların en büyük kısmını engelleyen element, bazı forum özelliklerini bozabilir. (Avatar yükleme sayfası gibi.) */
#catfish, /* Popup reklam */
.header-new .topmenu-new .sponsor, /* Topbar TTNET destek */
.header-new .logo-new .reklam, /* Header 728x90 */
.header-new .menu-new a[href="http://www.donanimhaber.com/reklam/"], /* Menü Reklam Ver */
.header-new .menu-new .sponsor, /* Menü Superonline */
table#Table3 td[width="135"], /* Anasayfa sağ bar */
tr[bgcolor="#ffffff"], /* Bölüm içi text reklam */
div.konulisteTextReklam, /* HepsiBurada Bar */
table[width="95%"] td[width="10%"][valign="top"][align="right"], /* Bölüm+Konu içi sağ bar */
table[width="95%"] td[width="10"], /* Konu içi sağ bar fix */
a[onclick="return getHepsiBuradaAds(this)"], /* Konu içi HepsiBurada butonu */
table#DHTEXT, /* DH kelime reklam */
table[style="margin:0 0 20px 0;"][width="700"], /* Footer sponsor */
div[style="width: 100%; min-width: 740px; display: inline-block; background: none repeat scroll 0% 0% rgb(241, 241, 241); border-bottom: 1px solid rgb(222, 222, 222);"], /* Footer reklamı fix */
div.alt-duzenlemeler div.facebook, /* Facebook fix */
#adklik_banner_div, #adklik_html_banner_div, /* AdKlik banner */
.firmamesaj /* Firmalar için konu içi sol profil */
{ display:none !important; visibility:hidden !important; } /* ^_^ */

/* Text reklamları engelle */
a span[onmouseout="hideAd();"] font,
span[onmouseout="hideAd();"] font b {
  border-bottom:0px !important;
  color:black !important;
  text-decoration:none !important;
  font-weight:normal !important;
  cursor: text !important;
}


/* Anasayfa sağ taraf fix */
table#Table3 table[width="95%"] {
  width: 100% !important;
}

/* Anasayfa ve bölüm içi bölüm ikonunu ortala */
table[width="95%"] td[bgcolor="d0d0d0"] table[width="100%"] td[width="3%"] {
  vertical-align: middle !important;
}

/* Bölüm sağ bar fix */
table[width="95%"] th[width="85%"] table[width="99%"] {
  width: 100% !important;
}

/* TTNET arkaplan fix & PM fix */
body {
  background-image: none !important;
  padding: 0 0 10px 0 !important;
  width: 100% !important;
}

/* Sayfa no. fix */
th[width="75%"]>table[width="95%"] {
  width: 100% !important;
}

/* Konu içi 2px aralık fix */
div#contentdiv table[width="100%"][border="0"][cellspacing="1"][cellpadding="4"], form[name="multidelete"][style="margin:0px 0px 0px 0px"] {
  margin-top: -1px !important;
}

/* Bölüm içi firma logosu fix */
tr[bgcolor="F1F1F1"] td.c2 a[href*="showProfile.asp"] img {
  max-height: 26px !important;
}

/* Bölüm içi firma logosu fix (üstkonu) */
tr.ustkonu[bgcolor="#f9f9f9"] td.c2 a[href*="showProfile.asp"] img {
  max-height: 21px !important;
}

/* Yeni konuları kalın göster */
tr[bgcolor="F1F1F1"] td .newpost a b {
  font-weight: bold !important;
}

/* Eski konuları ince göster */
tr[bgcolor="F1F1F1"] td .oldpost a b {
  font-weight: normal !important;
}

/* Yeni konu aç başlık ikonlarını tek satırda göster */
img[src="http://img.donanimhaber.com/micons/m8.gif"] + br {
  display: none !important;
}

/* Forum konusu */
a[href="/m_81182308/tm.htm"] {
  color: red !important;
}

/* Alıntılar */
.quote {
  width: auto !important;
  border-radius: 15px !important;
  padding: 7px !important;
}
/* Kod blokları */
pre {
  width: 800px !important;
}

pre:before {
  content: "code: \A \A";
  color: #505050;
  font: italic 11px Verdana ;
}

/* Konu ici butonlar */
.mButon.info {
  margin: 0 !important;
}

/* Konu ici profil
.singlepost-left .info {
  max-width: 100px;
  word-wrap: break-word;
}*/

/* Bölüm içi username fix */
tr[bgcolor="F1F1F1"] td.info, tr[bgcolor="F1F1F1"] td.c2 a {
  width: 170px !important;
  display: table !important;
}

/* Bu mesaj... bilgisi */
span[class="info"] {
  color: gray !important;
}

/* Konu başlıkları */
span[class="messagetitle"], span[class="forumtitle"] {
  font-weight: bold;
}

