/*Colour Scheme Choice*/
/*[[colorscheme]]*/

/*---------------------*/
/*   main theme start  */
/*---------------------*/
    

@-moz-document domain("twitch.tv"), domain("twitchtv.com") {
.emoticon-selector-toggle,
.ffz-ui-toggle{
    opacity:0.05!important;
    -webkit-transition:opacity 0.25s ease-in;  
    -moz-transition:opacity 0.25s ease-in;  
    -o-transition:opacity 0.25s ease-in;  
    transition:opacity 0.25s ease-in;  
}
    
.emoticon-selector-toggle:hover,
.ffz-ui-toggle:hover{
    opacity:1!important;;
    -webkit-transition:opacity 0.25s ease-in;  
    -moz-transition:opacity 0.25s ease-in;  
    -o-transition:opacity 0.25s ease-in;  
    transition:opacity 0.25s ease-in;  
}

/*[[jewchat]]*/

/* BetterTTV shit */
#channel .player-column #player{
    background:none!important;
}

#chat_line_list .line p{
    text-indent:0px!important;
}

/* Has Subscribed arrow removal */
li.line.fromjtv div.indicator{
display:none!important;
}

/* emotfix start */
.emoticon {margin-bottom:2px!important;}

/* Backgrounds off */
#custom_bg,#custom-bg {
    display:none!important;
}

