css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com") , url-prefix("https://www.tumblr.com") {

/*UNIVERSAL*/
/*icons*/
#new_post a[class="new_post_label"] span {
  position: absolute !important;
/*  background-repeat: no-repeat !important;*/
  margin-left: 6px !important;
  background: url("http://25.media.tumblr.com/tumblr_me4l5uJC8W1qlzdwqo1_500.png") !important;
  width: 64px !important;
  height: 64px !important;
}
/*font*/
#new_post a[class="new_post_label"] {
  padding-top: 72px !important;
}


/*DEFAULT STATE*/
#new_post a[class="new_post_label"],
#new_post a[class="new_post_label"] span {
  opacity: .9 !important;
  color: rgba(0,0,0,.65) !important;
}
/*HOVER STATE*/
#new_post a[class="new_post_label"]:hover,
#new_post a[class="new_post_label"] span:hover {
  opacity: 1 !important;
  color: rgba(0,0,0,1) !important;
}

/*ICONS - DIMENSIONS (for this style) 72px X 72px*/
/*TEXT ICON IMAGE*/
#new_post #new_post_label_text .new_post_label_icon  {
/*[[TEXTICON]]*/
}

/*PHOTO ICON IMAGE*/
