/*[[PHOTOICON]]*/
}

/*QUOTE ICON IMAGE*/
#new_post #new_post_label_quote .new_post_label_icon {
/*[[QUOTEICON]]*/
}

/*LINK ICON IMAGE*/
#new_post #new_post_label_link .new_post_label_icon {
/*[[LINKICON]]*/
}

/*CHAT ICON IMAGE*/
