/*[[CHATICON]]*/
}

/*AUDIO ICON IMAGE*/
#new_post #new_post_label_audio .new_post_label_icon {
/*[[AUDIOICON]]*/
}

/*VIDEO ICON IMAGE*/
#new_post #new_post_label_video .new_post_label_icon {
/*[[VIDEOICON]]*/
}
}
