css:
@-moz-document url-prefix("https://www.youtube.com"), url-prefix("http://www.youtube.com") {
#body-container {
    background: url("/*[[link]]*/") !important; 
    background-position: /*[[x]]*/ /*[[y]]*/ !important;
    background-size: 100% auto !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
    background-color: transparent !important;
}

#yt-masthead-container {
    background: rgba(255,255,255,0.5) !important;
    border: none !important;
}

#guide-container {
    background: rgba(255,255,255,0.5)!important;
    min-height: 370px !important;
    max-height: 600px !important;
    padding: 10px 5px !important;
    margin-right: 5px !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
}

.branded-page-v2-col-container {
    background: none !important;
    border: none !important;
}

.branded-page-v2-detached-top .branded-page-v2-primary-col {
    border: none !important;
}

#feed li.feed-item-container  {
    background: rgba(255,255,255,0.6)!important;
    padding: 20px 0 !important;
    margin-top: 5px !important;
    border: none !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
}
#feed li.feed-item-container:hover  {
    background: rgba(255,255,255,0.9)!important;
}

.branded-page-v2-secondary-col  {
    background: rgba(255,255,255,0.5)!important;
    padding: 10px 5px !important;
    margin-left: 5px !important;
    margin-top: 20px !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
}

.site-left-aligned #footer-hh {
    -webkit-transition: none !important;
}

body #footer-hh-container  {
    background-color: rgba(255,255,255,0.5) !important;
    border: none !important;
}

#footer-hh-main  {
    border: none !important;
}

/* video page */

#page.watch #guide-container {
    display: none !important;
}

#guide-container h3 {
    color: rgba(50,50,50,1) !important;
}

#watch7-content {
    background: none !important;
    border: none !important;
    margin-top: 10px !important;
}

#watch7-content > .yt-uix-button-panel {
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
}

#watch7-headline, #watch7-notification-area, #watch7-user-header {
    background: rgba(255,255,255,0.65) !important;
    border: none !important;
}

#watch7-action-buttons {
    background: rgba(255,255,255,0.7) !important;
    border: none !important;
}

#watch7-action-panels {
    background: rgba(255,255,255,0.65) !important;
    border: none !important;
}

#watch7-discussion {
    border: none !important;
    margin-top: 10px !important;
}

#comments-view .comment-list .comment, #comments-view .video-list {
    background: rgba(255,255,255,0.3)!important;
    padding: 15px 10px 5px !important;
    border: none !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
}

#comments-view .comment-list .comment:hover , #comments-view .video-list:hover {
    background: rgba(255,255,255,0.8)!important;
}

#comments-view h4, #comments-view h4 {
    background: rgba(255,255,255,0.3)!important;
    padding: 5px 10px 5px !important;
    border: none !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    color: rgb(255,255,255) !important;
}
#comments-view h4, #comments-view h4 a {
    color: rgb(255,255,255) !important;
}

.video-list-item {
    background: rgba(255,255,255,0.5)!important;
    padding: 5px 0 5px !important;
    border: none !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
}

#watch7-sidebar .video-list-item .stat {
    color: #555 !important;
}

/* upload page */

#upload-sidebar {
    background: rgba(255,255,255,0.3)!important;
    padding: 5px 10px 5px !important;
    border: none !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    color: rgb(255,255,255) !important;
    margin-top: 10px;
    margin-left: 10px;
}
/* Channel */
#branded-page-body-container {
    background: none !important;
}
}
