  html[dir=rtl] > body,
  body[dir=rtl],
  div[dir=rtl],
  span[dir=rtl],
  p[dir=rtl],
  ._6nm,
  ._1mk,
  .direction_rtl,
  ._-kb p[dir=rtl],
  ._-kb div[dir=rtl],
  ._5wj- div[dir=rtl],
  .webMessengerMessageGroup ._38 p
   {
	font-family: 'Mehr Nastaliq Web','Urdu Typesetting','Alvi Nastaleeq',Tahoma !important;
	font-size: 150% !important;
	line-height: 150% !important;
 }

