css:
@namespace html url("http://www.w3.org/1999/xhtml");
@-moz-document regexp('^(?!http://.*55ch\\.org/.*/src/)http://.*55ch\\.org/.*$') {

/*scrollbars*/
/*[[scroll]]*/

/*Cleanup*/
hr,
div.adminbar,
.loading-visible,
table.pages input,
td.postblock,
td.rules span,
div span[style="float:right"],
div.thread span[style="float:right"],
div.postarea hr,
body center spam,
body center img,
div.replymode,
img.likebutton,
img.tweetbutton,
div.footer,
.logo+hr,
.logo+hr+div+hr,
hr+script+center>a,
hr+script+center+hr,
#recaptcha_logo,
#recaptcha_tagline,
table+br+hr+center>a,
table+br+hr+center+hr,
.postarea>form>div[style]>a[target],
.doubledash {
display:none
}
input[accesskey="n"] {
display: none
}
/*General*/

* {
    border: none !important;
    background: none !important;
    font-size: /*[[fontamanho]]*/px !important;
    font-family: Terminus !important;
    text-decoration: none !important;
    font-weight: normal !important;
}

/*Mascot*/

/*[[barra]]*/

/*Post Box*/
/*[[postautohide]]*/



table.postform td {
padding: 0 !important
}

/*[[textformexpand]]*/

table.postform input {
    max-width: 180px; 
    opacity: .9;
    -moz-appearance: none;
    border-radius: 0px !important;
    margin-right: 2px;
}

input[accesskey="e"] {
width: 200px
}

input[type="submit"] {
margin-left: -3px;
margin-right: 0px !important;
width: 60px
}

input#spoiler {
    position: fixed;
    margin-top: 27px;
    margin-left: 63px
}

table.postform label {
position:fixed;
margin-top: 26px;
margin-left: 80px
}

input[type="password"], input[name="em"], input[name="subject"], input[name="name"]{
margin-left: 1px
}


select[name="embedtype"] {
    -moz-appearance: none;
    margin-left: -3px
}

input[name="embed"] {
max-width: 126px !important;
margin-left: 1px;
}

input[name="imagefile"] {
max-width: 126px !important
}

td a.rules {
display: none !important
}

/*Catalog*/
td.rules li [href]{
    display:none !important
}

td.rules {
display:none !important
}

/*hides*/
input[value="Próximo"] {
display:none
}
input[value="Anterior"] {
display:none
}

/*Watch Threads*/
div.watchedthreads {
    right:3px;
    min-width: 248px;
    position: fixed !important;
    border-radius: 0px
    }

/*Boards*/
div.navbar {
    right: 256px !important;
    position: fixed;
    padding: 0px !important;
    text-align: center;
    top: -1px !important; left: 0px;
}



/*Delete & Report*/
table.userdelete {
    max-width: 260px !important;
    height: 85px !important;
    position: fixed;
    right: -4px;
    top: -1px;
    padding-bottom: 4px;
    opacity: 0.0;
    -moz-box-sizing: border-box !important;
    -moz-transition: right .3s ease-in-out 1s, opacity .3s ease-in-out 1s;
}

table.userdelete:hover {
    max-width: 260px !important;
    height: 85px !important;
    position: fixed;
    opacity: 1;
    -moz-transition: all 0.5s ease;
}

table.userdelete input {
    -moz-appearance: none;
}

/*Logo & Descrição*/
div.logo {
    position: fixed;
    max-width: 250px;
    min-width: 250px;
    min-height: 135px;
    top: 17px; right: 3px;
    border-radius: 0px;

}

div.logo img {
    margin: -1px !important;
    max-width: 249px;
    height: auto !important;
    margin-top: -16px !important;
    margin-right: 2px !important;
    }

body center {
    display: none
}

/*Sizing*/

span.extrabtns img {
    max-height: 11px;
    background-size: 11px !important;
    border-radius: 1px;
}
form div table, form table {
    width: 100%
}

/*Não sei o motivo, mas sem esse padding alguns posts ficam desalinhados*/
form div div {
padding: 1px;
}


body, form {
    margin-right: 124px !important;
    margin-top: 17px !important;
}

td.highlight {
    padding-left: 3px !important;
    padding-bottom: 2px;
}

img.thumb {
    padding-left: 3px !important;
    padding-bottom: 2px;
}

td.highlight span.filesize {
    padding-left: 23px
}

td.highlight label {
    padding-left: 3px
}

blockquote {
    margin-left: 20px !important;
    padding-bottom: 5px;
    margin-top: 5px
}

/*Misc*/
div#updater.reply.dialog {
    position: fixed !important;
}

div#updater input {
    -moz-appearance: none;
}


.reply {
    border-radius: 0px;
}

div.reflinkpreview {
    border-radius: 0px;
}

a[href="mailto:sage"] {
    text-decoration: none !important;
}


a[href="mailto:sage"]:before {
    content: 'sage ';
}

/*[[coresefont]]*/


}
