*/*[[backgroundsize]]*/

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/spotlight/"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/login"), url-prefix("http://www.tumblr.com/likes") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: transparent!important;

}

#right_column {

background-color: #000000!important;

background-image: none!important;

}
}

@-moz-document url-prefix("http://www.tumblr.com/queue") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: transparent!important;

}

#right_column {

margin-right: -430px!important;

position: fixed!important;

right: 50%!important;

background-color: rgb(0,0,0,0.4)!important;

background-image: none!important;

}
}

@-moz-document url-prefix("https://www.tumblr.com/settings"), url-prefix("http://www.tumblr.com/following"), url-prefix("http://www.tumblr.com/explore"), url-prefix("https://www.tumblr.com/lookup"), url-prefix("http://www.tumblr.com/reblog/"), url-prefix("http://www.tumblr.com/tumblelog/"), url-prefix("http://www.tumblr.com/edit/") {
body {background-image: url('http://i45.tinypic.com/2u53j4k.jpg');
background-position: bottom left;
background-attachment: fixed;}
}

@-moz-document url-prefix("https://www.tumblr.com/login") {
body {

background-color: #000000;

background-image: url('http://i45.tinypic.com/xemkch.jpg');

background-position: bottom left;

background-repeat: no-repeat;

background-attachment: fixed;

}



#right-column div {

background-color: rgb(0,0,0,0.4)!important;

}



.dashboard_nav_item {

background-image: none!important;

background-color: rgb(0,0,0,0.4)!important;

padding: 10px 5px!important;

margin: 0px!important;

border-radius: 10px;

}



.new_post_notice {

background-color: #d26b77!important;

border: 0px!important;

color: #000000!important;

border-bottom-right-radius: 3px 3px;

}



.new_post_notice img {

opacity: 0.9;

}



.answer_container {

border: 0px!important;

background: rgba(0, 0, 0, 0.7)!important;

}



ol#posts li.post ul {

list-style-type: square;

-webkit-padding-start: 20px;

}



.notification .alt, li.notification {

background-color: rgba(0, 0, 0, 0.5)!important;

border: 0px!important;

color: #a8a9a9!important;

}



.notification a {

border: 0px!important;

}



#auto_pagination_loader {

background: transparent!important;

border: 0px!important;

font-weight: normal;

font-family: Georgia, serif;

font-style: italic;

color: black;

}



#auto_pagination_loader img { display: none; }



.post_avatar {

border: 0px!important;

backgrouund: transparent!important;

border-radius: 10px!important;

border-top-right-radius: 0px!important;

border-bottom-left-radius: 0px!important;

}



#left_column #posts .post .permalink {

background-color: #1A1A1A!important;

border-top-right-radius: 9px!important;

}



li.post {

border: 0px!important;

}



ol#posts li.post blockquote {

padding-left: 10px;

border-left-width: 3px;

}



ol#posts li.post span.quote {

font-size: 130%;

font-style: italic;

font-family: georgia, serif;

}



#nav .nav_item {

text-shadow: #000000 0px 0px 5px;

}



#nav .active {

text-shadow: none!important;

}



#dashboard_switch_blog_arrow_current {

background-image: none!important;

}
}

