css:
@-moz-document domain("ebay.com") {

/* Remove ADs */
#HP-EbayFeedback, .topBnrSc, [title="ADVERTISEMENT"], A[onclick*="WhatsThisDcAdPopUp"], .sbx-c.ts-pd, .sdcBox, #adItems, [id^="rtm_html_"], .pt-tray, [href*="adfarm"], .topBnrSc, #gh-doodleS, .answers, .s-feedback, .srp-rail__right, .topBannerAd {
  display: none !important;
}

/* Remove promoted search results */
#ListViewInner > li[r="1"] {
  display: none !important;
}
#ListViewInner > li:first-of-type {
  display: list-item !important;
}

/* Widen search results */
.rr_present, .srp-river, .s-answer-region-center-top {
  width: 1073px !important;
}

/* Widen old myebay */
#myebaypage[style^="width:974px"] {
  width: 1200px !important;
}
#CentralArea[style="width:754px"] {
  width: 980px !important;
}

/* Remove bordering filler */
#searchWidthDiv, #RightPanel {
  display: none !important;
}

/* Remove recommended items */
div[id^="merch_html_"], #purchase-history div[id^="merch_html_"], #ItemDisplayContainer_MyeBayNextSellingReminders {
  display: none !important;
}

}
