css:
@namespace xul url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@namespace url("http://www.w3.org/1999/xhtml");

/* Strings for l10n */
/*[[lang]]*/
/* End strings */

@-moz-document url-prefix("about:neterror") {
  #errorPageContainer {
    background-image: url("chrome://global/skin/icons/warning-large.png"),
                      url("http://lh5.googleusercontent.com/-k7Q98e64PbA/UP8odh-bqmI/AAAAAAAADI8/rR4X27Be0so/s0/McKayla-384-t.png") !important;
  }
  #errorLongDesc li:last-child:after {
    display: list-item;
    margin-top: .5em;
  }
  #resurrect { background-color: transparent !important; }
}

@-moz-document url-prefix("about:certerror") {
  #errorPageContainer {
    background-image: url("chrome://global/skin/icons/sslWarning.png"),
                      url("http://lh5.googleusercontent.com/-k7Q98e64PbA/UP8odh-bqmI/AAAAAAAADI8/rR4X27Be0so/s0/McKayla-384-t.png") !important;
  }
  #getMeOutOfHereButton {
    text-transform: lowercase;
  }
  #getMeOutOfHereButton:before {
    text-transform: none;
  }
  #expertContent + div > p:nth-child(2):after {
    font-weight: bold;
  }
}

@-moz-document url-prefix("about:blocked") {
  #errorPageContainer {
    background-image: url("chrome://global/skin/icons/blacklist_large.png"),
                      url("http://lh5.googleusercontent.com/-k7Q98e64PbA/UP8odh-bqmI/AAAAAAAADI8/rR4X27Be0so/s0/McKayla-384-t.png") !important;
  }
  :root.blacklist #errorTitle, :root.blacklist #errorLongContent,
  :root.blacklist #errorShortDesc, :root.blacklist #errorLongDesc,
  :root.blacklist a {
  background-color: transparent !important;
  }
  #getMeOutButton {
    text-transform: lowercase;
  }
  #getMeOutButton:before {
    text-transform: none;
  }
}

@-moz-document url-prefix("about:neterror"),
               url-prefix("about:certerror"),
               url-prefix("about:blocked") {
  #errorPageContainer {
    background-position: 0 0, right bottom !important;
    background-repeat: no-repeat, no-repeat !important;
    background-attachment: scroll, fixed !important;
  }
  #errorPageContainer:after {
    content: "";
    background: transparent
                url("http://lh3.googleusercontent.com/-hxF0dhBj6u0/UMN_GW1eSlI/AAAAAAAAC-M/GhAwly8FOqc/s0/McKayla-384.png")
                bottom right no-repeat fixed;
    position: fixed;
    top: 0; right: 0; bottom: 0; left: 0;
    pointer-events: none;
    z-index: -1;
  }
}

@-moz-document url-prefix("http://userstyles.org/styles/81026") {
  #main-screenshot .screenshot { visibility: hidden; }
  #main-screenshot a { display: block; }
  #main-screenshot {
    background: url("http://lh3.googleusercontent.com/-1aGFkLH-Ck4/USg_e-VrR-I/AAAAAAAADOI/jt3aLh_mffQ/s0/mckayla-impressed.jpg")
                0 0 no-repeat;
  }
}*{}
