/*	Tumblr 'new post' icons by ohaixo [recycled]
	recycled Tumblr icons from ohaixo (http://userstyles.org/users/66455)
	by hide_header (http://userstyles.org/users/117864)
*/
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{

.new_post_label > I {
  color: transparent !important;
  transition: background-position .08s linear !important;
}
.new_post_label:nth-of-type(1) > I {background-position: 2px -5px !important}
.new_post_label:nth-of-type(2) > I {background-position: -73px -5px !important}
.new_post_label:nth-of-type(3) > I {background-position: -148px -5px !important}
.new_post_label:nth-of-type(4) > I {background-position: -223px -5px  !important}
.new_post_label:nth-of-type(5) > I {background-position: -298px -5px !important}
.new_post_label:nth-of-type(6) > I {background-position: -373px -5px !important}
.new_post_label:nth-of-type(7) > I {background-position: -448px -5px  !important}

.new_post_label:nth-of-type(1) > I {background-position: 2px -9px !important}
.new_post_label:nth-of-type(2) > I {background-position: -73px -9px !important}
.new_post_label:nth-of-type(3) > I {background-position: -148px -9px !important}
.new_post_label:nth-of-type(4) > I {background-position: -223px -9px  !important}
.new_post_label:nth-of-type(5) > I {background-position: -298px -9px !important}
.new_post_label:nth-of-type(6) > I {background-position: -373px -9px  !important}
.new_post_label:nth-of-type(7) > I {background-position: -448px -9px  !important}

.new_post_label:nth-of-type(1) > I {background-position: 2px -5px !important}
.new_post_label:nth-of-type(2) > I {background-position: -73px -5px !important}
.new_post_label:nth-of-type(3) > I {background-position: -148px -5px !important}
.new_post_label:nth-of-type(4) > I {background-position: -223px -5px !important}
.new_post_label:nth-of-type(5) > I {background-position: -298px -5px !important}
.new_post_label:nth-of-type(6) > I {background-position: -373px -5px !important}
.new_post_label:nth-of-type(7) > I {background-position: -448px -5px !important}

/*[[icons]]*/
}
