/*	Tumblr dashboard icons by ljsquid [recycled]
	recycled Tumblr icons from ohaixo (http://userstyles.org/users/78005)
	by hide_header (http://userstyles.org/users/117864)
*/
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{
A.new_post_label > I {height: 90px}
  .new_post_label > I {
  color: transparent !important;
    background: url("/*[[icons]]*/") no-repeat !important;
    background-size: auto !important;
    z-index: -1;
  }
  .new_post_label:nth-of-type(1) > I {background-position: 2px 0 !important}
  .new_post_label:nth-of-type(2) > I {background-position: -73px 0 !important}
  .new_post_label:nth-of-type(3) > I {background-position: -148px 0 !important}
  .new_post_label:nth-of-type(4) > I {background-position: -223px 0  !important}
  .new_post_label:nth-of-type(5) > I {background-position: -298px 0 !important}
  .new_post_label:nth-of-type(6) > I {background-position: -373px 0 !important}
  .new_post_label:nth-of-type(7) > I {background-position: -448px 0  !important}
  .new_post_label > I {
    top: 5px !important;
    -webkit-transition: top 0.08s linear !important;
    transition: top 0.08s linear !important;
  }
  .new_post_label:hover > I {top: 1px !important}
  .new_post_label {padding: 75px 0 4px 1px !important}
}
