css:
@charset "utf-8";
/* i really want this to be global */
/*
@name          楷体|绿色链接|input美化
@namespace     http://userstyles.org
@description	  楷体|链接、input美化
@author        独立观察员
@version       1.313
@homepage      http://userstyles.org/styles/82101/input
@license       Public Domain
@exclude       http://www.icoc.cc/
@run-at        document-start
*/

*{
    font-family: "楷体", "华文楷体","微软雅黑"!important ;
    font-weight: 600!important;
    letter-spacing : -1px!important;
}

select,textarea,#result_box,#in,.SG_connBody,.mo,.d_post_content_main,.results,.med{
    color: black !important;    
    background: /*[[hcolor]]*/ !important;
}
a:hover {color: /*[[mycolor]]*/!important;}
a:visited { color:/*[[vcolor]]*/!important ;}
@media print {
    *{
        color: black !important;    
        background: white !important;
    }
}

input[type="text"],input[type="password"],input[type="url"],input[type="search"],input[type="email"],.box-search_input,.s_ipt_wr,.s_ipt,.search-box-bg,.word,.input_span1,.i,.input-box,.queryborder,.lst-d,.q,.sw_b,.bi,.play-panel,.gn_search,#search-input,.hdsearch,.zu-top-search-form,.hdi,.topbar_input,.ip{
    color: black !important;
    background:#C7EDCC ;
    background-image: url(/*[[mypicture]]*/) !important;
    background-repeat: repeat!important; 
    background-position: center center!important;    
}
