css:
@-moz-document domain('www.evernote.com') {
    body > div > div:last-child > div > div:nth-child(3) > div > div:nth-child(3) > div > div:first-child > div > div:nth-child(2) {
        /* Replace to any width you want */
        width: /*[[nlwidth]]*/ !important;
    }

    body > div > div:last-child > div > div:nth-child(3) > div > div:nth-child(3) > div > div:first-child > div > div:last-child {
        /* Replace on width of previous style */
        left: /*[[nlwidth]]*/ !important;
    }

    .GLMQCVEBEY { width: auto !important; }
    .noteSnippetTitle { width: auto !important; }
}
