/* Likes */
.controls_section li .likes:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important; }


a.likes  div.hide_overflow{ visibility: hidden !important; }

.controls_section li .likes:before {  content: "/*[[likes]]*/" !important; }





/* Followers */
.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 92px !important;
  font: inherit !important; }

a.followers  div.hide_overflow{  visibility: hidden !important;}

.controls_section li .followers:before {  content: "/*[[followers]]*/" !important;}



/* Posts */
.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 92px !important;
  font: inherit !important; }

a.posts  div.hide_overflow{  visibility: hidden !important;}

.controls_section li .posts:before {  content: "/*[[posts]]*/" !important;}






}
