css:
@-moz-document url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/dashboard") {
/*
#markup
body
>div.l-container
->div.l-content
-->div#left_column
--->ol#posts
---->li.post_container     <--
----->div.post
------>div.post_avator
------>div.post_wrapper
------->div.post_header
------->div.post_content
------->div.post_cotent_inner
-------->div.post_media
-------->div.post_body
------->div.post_footer
*/

/*<main>*/
.l-container {
    width: auto !important;
    padding: 0 ;
}
.l-content {
    padding: 0 !important;
}
#left_column {
    float: none !important;
    width: auto !important;
    padding: 56px 0 0 0;
    position: relative;
}
#right_column {
    display: none;
}
#posts.posts {
    margin: 0;
    text-align:center;
}
/*newpost*/
#posts.posts>#new_post_buttons {
    display: block !important;
    width: 540px;
    margin: 4px auto;
    padding: 0;
}
#posts.posts>#new_post_buttons .post_avatar{
    display: none;
}
/*posts*/
#posts.posts>.post_container{
    position: relative;
    z-index: 8;
    display: inline-block !important;
    text-align: left;
    vertical-align: /*[[valign]]*/;
    padding: 0;
    margin: 4px  3px;
    max-height: /*[[post_height]]*/; /*default 85vh*/
    overflow: hidden;
}
#posts.posts>.post_container:hover{
    z-index: 10;
    overflow: auto;
}
#posts.posts>.standalone-ad-container{
    display: inline-block !important;
    margin: 4px 3px;
}
.post:not(.post_micro)>.post_avatar{
    display:none;
}
/*</main>*/


