css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("soundcloud.com") {

.sc-ministats.sc-ministats-small {
    font-size: 16px !important;
}

.sound__header .sc-ministats-reposts {
    color: /*[[repost-sound]]*/ !important;
}

.sc-ministats.sc-ministats-small.sc-ministats-plays > span,
.sc-ministats.sc-ministats-medium.sc-ministats-plays > span,
.sc-ministats.sc-ministats-large.sc-ministats-plays > span {
    color: /*[[stats-plays]]*/ !important;
}

.sc-ministats.sc-ministats-small.sc-ministats-likes > span,
.sc-ministats.sc-ministats-medium.sc-ministats-likes > span,
.sc-ministats.sc-ministats-large.sc-ministats-likes > span,
.sc-icon-like + span {
    color: /*[[stats-likes]]*/ !important;
}

.sc-ministats.sc-ministats-small.sc-ministats-reposts > span, 
.sc-ministats.sc-ministats-medium.sc-ministats-reposts > span,
.sc-ministats.sc-ministats-large.sc-ministats-reposts > span,
.sc-icon-repost + span {
    color: /*[[stats-reposts]]*/ !important;
}

.sc-ministats.sc-ministats-small.sc-ministats-comments > span,
.sc-ministats.sc-ministats-medium.sc-ministats-comments > span,
.sc-ministats.sc-ministats-large.sc-ministats-comments > span,
.sc-icon-comment + span {
    color: /*[[stats-comments]]*/ !important;
}

.sc-ministats.sc-ministats-small.sc-ministats-followers > span,
.sc-ministats.sc-ministats-medium.sc-ministats-followers > span,
.sc-ministats.sc-ministats-large.sc-ministats-followers > span,
.sc-icon-following + span {
    color: /*[[stats-followers]]*/ !important;
}

.sc-ministats.sc-ministats-small.sc-ministats-sounds > span,
.sc-ministats.sc-ministats-meduim.sc-ministats-sounds > span,
.sc-ministats.sc-ministats-large.sc-ministats-sounds > span {
    color: /*[[sounds]]*/ !important;
}

.soundTitle__username {
    font-size: /*[[username-fontsize]]*/ !important;
    color: /*[[username]]*/ !important;
}

.sound__uploadTime {
    font-size: /*[[uploadtime-fontsize]]*/ !important;
    color: /*[[uploadtime]]*/ !important;
    top: 0px !important;
}

.timeIndicator__total {
    background: none repeat scroll 0 0 /*[[totaltime-background]]*/ !important;
}

.timeIndicator__total > span:last-child {
    font-size: /*[[totaltime-fontsize]]*/ !important;
    color: /*[[totaltime]]*/ !important;
}

.muteButton {
    color: red !important;
}

}
