css:
@-moz-document url-prefix("https://feedly.com/"), url-prefix("http://feedly.com/"), url-prefix("https://www.feedly.com/"), url-prefix("http://www.feedly.com/") {
* {
    font-family: Helvetica, Arial, sans-serif;
}
.fx .entry.u0 .content .title {
    font-size: 15px !important;
}
.fx .entry .summary {
    font-size: 15px !important;
}
.feedIndexTitleHolder {
    font-size: 14px !important;
}
body {
    background: none repeat scroll 0% 0% #181818 !important;
}
#feedlyPageHolderFX, #headerBarFX header {
    background-color: #181818 !important;
}
#feedlyFrame, #feedlyPage {
    width: auto !important;
}
#feedlyPart {
    width: 100% !important;
    margin-top: 25px !important;
    padding-right: 0 !important;
}
#mainArea {
    width: auto !important;
    margin-right: 50px !important;
    margin-top: 15px !important;
}
#mainArea > div {
    color: #999 !important;
}
.summary {
    max-width: 80% !important;
    color: #999999 !important;
}
.area, .inlineFrame {
    background-color: #181818 !important;
    border: 0 solid rgba(51, 51, 51, .4)!important;
}
#mainBar {
    width: auto !important;
}
#sideArea .feedIndexTitle {
    color: #999999 !important;
}
#sideArea {
    position: absolute !important;
    margin-right: 10px !important;
    overflow: visible !important;
    right: 0 !important;
}
.fusionAdLink {
    border-color: #333333 !important;
    background-color: #222222 !important;
}
.u4Entry {
    margin-bottom: 18px !important;
    max-width: none !important;
}
#feedlyPart0 {
    margin-top: 5px !important;
    margin-left: 10px !important;
}
#feedlyPart0.area {
    padding-left: 5px !important;
}
#feedlyPageHeader {
    margin-top: 0 !important;
}
.headerInfo {
    background-color: #181818 !important;
    border: 1px solid #222 !important;
}
.headerInfo.headerInfo-article {
    margin-top: 0 !important;
}
h2 {
    margin-top: 18px !important;
    margin-bottom: 12px !important;
}
.condensed .inlineFrame {
    padding-left: 40px !important;
    padding-right: 40px !important;
}
#section0 > div > div {
    height: 0 !important;
}
.infoBox {
    margin-bottom: 30px !important;
    margin-top: 30px !important;
}
#storeHolder.picturePicture {
    background-color: #181818 !important;
    color: #cccccc !important;
    border-color: #222222 !important;
}
.wide #storeHolder.picturePicture {
    margin-left: 80% !important;
}
#store .suggestion {
    border-bottom-color: #222222 !important;
    height: 16px !important;
}
#storeFrame {
    background-color: #181818 !important;
}
#store .separator {
    background-color: #527b08 !important;
}
#store .result {
    color: #2bb24c !important;
    border-bottom-color: #333333 !important;
}
#feedlyDialogFramePart {
    background-color: #181818 !important;
    color: #cccccc !important;
}
.hAction.secondary, .hAction.tertiary {
    background-color: #222222 !important;
}
.fieldLabel {
    color: #2bb24c !important;
}
.checkboxLabel {
    color: #999999 !important;
}
#feedlyCenter {
    background: none repeat scroll 0% 0% #181818 !important;
}
#feedlyFrame {
    background-color: #181818 !important;
}
.u0Entry {
    border-color: #181818 !important;
    margin-left: -2px !important;
}
.u0Entry:hover {
    background: none repeat scroll 0% 0% #222222 !important;
}
.u0Entry.lastModified {
    color: #2bb24c !important;
}
.u0Entry.quicklisted {
    background-color: #181818 !important;
    border-color: #30460a !important;
}
#feedlyTitleBar h1 {
    font-style: normal !important;
    color: #2bb24c !important;
}
h1, h2, h3, h4, h5, h6 {
    color: #2bb24c !important;
}
.entryTitle {
    color: #2bb24c !important;
}
.entryBody {
    color: #cccccc !important;
    margin-top: 5px !important;
    max-width: none !important;
}
.entryBody a {
    color: #2bb24c !important;
}
.action {
    color: #2bb24c !important;
}
.nonEmpty {
    color: #cccccc !important;
}
.viewerIcon {
    background-color: #181818 !important;
    color: #999999 !important;
}
#who, #systemBar {
    color: #999999 !important;
}
.fusionAD > div {
    color: #2bb24c !important;
}
.metadata > span {
    color: #2bb24c !important;
}
.categoryUnreadCount {
    opacity: 1 !important;
}
.entryBody .summary b, .entryBody .content b {
    color: #cccccc !important;
}
.content img {
    border: 0 !important;
    margin-top: 0 !important;
    border-radius: 0 !important;
}
.empty {
    color: #999999 !important;
}
.home {
    color: #999999 !important;
}
.notSubscribed .read {
    color: #666666 !important;
}
.visual {
    border-color: #333333 !important;
}
.u11Entry {
    background-color: #30460a !important;
}
.topRecommendedEntry {
    width: 300px !important;
}
.placeholder {
    background-color: #181818 !important;
}
#feedlyTabs {
    background-color: #181818 !important;
}
#feedlyTabsHolder {
    color: #999999 !important;
    margin-top: 5px !important;
    background-color: #181818 !important;
}
#feedlyTabsHolder .target.selected {
    color: #2bb24c !important;
    background-color: #181818 !important;
}
.navSelector {
    border-bottom-color: #333333 !important;
}
.navSelector .selected {
    border-bottom-color: #30460a !important;
}
#floatingBar {
    background-color: #181818 !important;
    border: 1px solid #222222 !important;
}
#floatingBar h1 {
    border-bottom-color: #181818 !important;
    color: #2bb24c !important;
}
.floatingEntryScroller {
    background-color: #181818 !important;
}
.slideBumper div {
    opacity: .8 !important;
}
#herculeHint, .herculeResult, #herculePanel {
    background-color: #222222 !important;
    color: #cccccc !important;
    border-color: #333333 !important;
}
#herculeInput {
    color: #cccccc !important;
}
#floatingTitleBar {
    color: #cccccc !important;
}
.u100Entry {
    background: none repeat scroll 0% 0% #181818 !important;
    min-height: 80px !important;
    max-width: none !important;
}
.u100Entry .title {
    margin-bottom: 14px !important;
    max-width: none !important;
}
.u100Frame {
    border: none !important;
    padding: 10px !important;
}
.u100Frame .selectedEntry {
    background-color: #181818 !important;
}
.u5Entry {
    border-color: #333333 !important;
    background: none repeat scroll 0% 0% #222222 !important;
}
.u5Entry .title {
    background-color: #181818 !important;
}
div .wikibar {
    border-top-color: #333333 !important;
}
.entryholder {
    background-color: #181818 !important;
    border: 0 !important;
}
.metadata {
    color: #666666 !important;
}
.websiteCallForAction {
    background-color: #222222 !important;
    border-color: #333333 !important;
}
.toBeTagged {
    background-color: #181818 !important;
    color: #cccccc !important;
}
.popup {
    background-color: #181818 !important;
    border-color: #333333 !important;
}
.popup .option {
    color: #999999 !important;
}
.popup hr {
    border-color: #666666 !important;
}
#popupPart {
    color: #999999 !important;
}
.bottomWikiWidget {
    margin-top: 25px !important;
}
.topWikiWidget {
    border-bottom: 1px solid #222222 !important;
    margin-bottom: 5px !important;
    padding-bottom: 5px !important;
}
.wikiWidgetAction {
    background-color: #181818 !important;
}
.subscriptionContentsHolder {
    background-color: #181818 !important;
    border: 1px solid #222222 !important;
}
.subscriptionContentsHolder:hover {
    border: 1px solid #2bb24c !important;
}
.preferencesItemLabel {
    color: #cccccc !important;
}
.preferencesItemDescription {
    color: #999999 !important;
}
.preferencesPanel label {
    color: #999999 !important;
}
#feedlyTabsHolder .icon {
    opacity: .4 !important;
}
#feedlyTabsHolder:hover .icon {
    opacity: 1 !important;
}
.twitterTimeLine {
    background-color: #181818 !important;
}
div.object {
    background-color: #181818 !important;
    border-color: transparent !important;
}
.socialPost {
    background-color: #181818 !important;
    border-color: #333333 !important;
}
.message {
    color: #cccccc !important;
}
.message b {
    color: #2bb24c !important;
}
.message span:nth-of-type(2) {
    color: #527b08 !important;
}
.lessThanAnHour {
    color: #999999 !important;
}
#sideArea a {
    color: #2bb24c !important;
}
.proShowcase {
    background-color: #222222 !important;
}
#feedlySignPart {
    top: 240px !important;
}
.secondaryCallForAction, .websiteCallForAction {
    border-color: #333333 !important;
}
.frameActionsTop {
    background-color: #181818 !important;
}
#bigMarkAllAsRead {
    margin-top: 50px !important;
}
#bigMarkAllAsReadButton {
    background-color: #222222 !important;
    border-color: #333333 !important;
}
.secondaryCallForAction:hover {
    color: #cccccc !important;
    border-color: #30460a !important;
}
.condensedTools a {
    opacity: .5 !important;
}
.condensedTools a:hover {
    opacity: 1 !important;
}
.quickGoto {
    background-color: #181818 !important;
    color: #cccccc !important;
}
.hot {
    color: orange !important;
}

/* official color:  #2bb24c */
 .button-markasread:hover:before, .button-refresh:hover:before, .icon-fx-cross-ios-sm-black:hover:before, .save-for-later:hover:before, .button-customize-page:hover:before, .fx .mark-as-read:hover:before, .fx .hide:hover:before {
    color: #2bb24c !important;
}
.fx .condensed-tools button {
    height: 40px !important;
    width: 40px !important;
}
.fx .entry.u0 .save-for-later {
    height: 40px;
}
#mainBarFX, #headerBarFX, #searchBarFX {
    background-color: #181818 !important;
}
#headerBarFX header, #headerBarFX .row {
    max-width: 100% !important;
}
.fx .entry .title {
    color: #ccc !important;
}
.fx .entry.u0 {
    border-top: 1px solid #333 !important;
}
.fx .entry.u0:hover {
    background-color: #333 !important;
}
.fx.popover, .fx .popover {
    background-color: #181818;
    border: 1px solid #333;
}
.fx .menu .item-header, .fx .menu .item {
    color: #999 !important;
}
.fx .menu .item:hover, .fx .menu .item.active, .fx .menu .board-tag:hover .item.delete-tag, .fx .board-tag:hover .menu .item.delete-tag {
    background-color: #333 !important;
    color: #fff !important;
}
.fx .entry.u0.selected, .fx .entry.u100.selected {
    border: 1px solid #333 !important;
}
.fx .entry.u0.quicklisted {
    background-color: #333 !important;
}
.fx .inlineFrame {
    border: none !important;
    box-shadow: none !important;
}
.unread, .fx .entry.unread .title {
    color: #cccccc !important;
}
.read, .fx .entry.read .title, .fx .entry.read .summary {
    color: #666666 !important;
}
/* Improve card view */
 .fx .entry.u5:nth-child(3n+1) {
    clear: none;
}
.fx .container {
    max-width: none;
}
.fx .entry.u5 {
    margin-left: 0;
    margin-right: 0;
    margin-bottom: 20px;
    margin-top: -8px;
    padding: 4px;
    width: 16.5% !important;
}
.fx h4, h4.fx, .fx-h4 {
    font-size: 1.0rem !important;
}
/* Improve magazine view */
 .fx .entry.u4 {
    max-width: none;
    padding: 0;
}
.summary {
    max-width: 100% !important;
}
::-webkit-scrollbar {
    background-color: #222 !important;
}
::-webkit-scrollbar-thumb {
    background-color: #333 !important;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 1px rgba(0, 0, 0, 1);
}
/* layout button dropdown */
 .fx .page-layout-menu.form .field .label-radio {
    color: #ccc
}
/* dropdown button */
 .fx .button.secondary {
    border: 1px solid #333;
}
.fx .button.secondary:hover {
    border: 1px solid #666;
}
/* visit website button */
 .entryholder .fx-button.secondary.visitWebsiteButton, .fx button.giant-mark-as-read {
    border-color: #333 !important;
}
/* visit website button hover */
 .entryholder .fx-button.secondary.visitWebsiteButton:hover, .fx button.giant-mark-as-read:hover
/* mark as read button big hover */
 {
    border-color: #666 !important;
    background-color: #333 !important;
    color: #ccc !important;
}
/* share more menu */
 .headerInfo-arrow {
    filter: grayscale(100%) invert(62%) !important;
}
.headerInfo-expanded section, .headerInfo-expanded section:hover {
    color: #ccc;
}
.headerInfo-expanded section {
    border-color: #333 !important;
}
.headerInfo-expanded section:hover {
    background-color: #333333 !important;
}
/* image border Magazine View */
 .fx .entry.u4 {
    border-left: none !important;
}
.fx .form.hercule-search input[type="text"] {
    background-color: #333 !important;
}
/* sidebar add content */
 .button-icon-left {
    background: #333 !important;
}
.menu.menu-custom {
    background: #181818;
    border: 1px solid #333;
}
/* USER SETTINGS AREA */

/* user menu */
 .profile-bubble-menu {
    background: #181818;
}
/* floating tab */
 .floatingEntryContent-tab {
    filter: grayscale(100%) invert(90%);
}
.fx .form .field label, .fx .form .field .label, .fx .picker .item-header {
    color: #999;
}
.fx .profile-bubble .user {
    background-color: #333;
}
.fx .profile-bubble .user .item-header, .fx .profile-bubble .user .item-meta {
    color: #2bb24c;
}
/* settings card*/
 .list-cards .card {
    box-shadow: none !important;
    background-color: #333;
}
.preference-sections p, .preference-sections .fx .feed-preview ul, .fx .feed-preview .preference-sections ul, .theme-panel .theme-panel-label {
    color: #999;
}
.fx table {
    border: 1px solid #333;
}
}
