css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {
/*****************************************************
 * Random coloring and small positioning adjustments *
 *---------------------------------------------------*/

/*** 28.6.2013 ***************************************************/
#watch7-main
{
	max-width: 1100px !important;
}

/*** 17.6.2013 ***************************************************/

.yt-badge
{
	border: 1px solid #00BD00 !important;
	color: #BEBEBE !important;
}

.yt-uix-button-subscribe-unbranded,
.yt-uix-button-subscribe-branded,
.yt-uix-button-context-light .yt-uix-button-subscribed-unbranded,
.yt-uix-button-context-light .yt-uix-button-subscribed-branded,
.yt-subscription-button-subscriber-count-unbranded-vertical,
.yt-subscription-button-subscriber-count-branded-vertical,
.yt-subscription-button-subscriber-count-unbranded-horizontal,
.yt-subscription-button-subscriber-count-branded-horizontal
{
	background-color: #2B2B2B !important;
	background-image: none !important;
	box-shadow: inset 0px 0px 2px #656565 !important;	
}

.yt-uix-button-subscribed-unbranded,
.yt-uix-button-subscribed-branded
{
	background: #2B2B2B !important;
	box-shadow: inset 0px 0px 5px #00BD00 !important;
	border: none !important;
}

.yt-uix-button-subscribed-unbranded.hover-enabled:hover,
.yt-uix-button-subscribed-branded.hover-enabled:hover
{
	box-shadow: none !important;
}

.yt-uix-button-subscribed-unbranded .yt-uix-button-content,
.yt-uix-button-subscribed-branded .yt-uix-button-content
{ 
	color: #FFFFFF !important;
	text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.447) !important;
}

.yt-uix-button-subscribed-unbranded.hover-enabled:hover .yt-uix-button-content,
.yt-uix-button-subscribed-branded.hover-enabled:hover .yt-uix-button-content
{
	color: #FFFFFF !important;
	text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.506) !important;
}

.yt-uix-button-subscribed-unbranded .yt-uix-button-icon-subscribe,
.yt-uix-button-subscribed-branded .yt-uix-button-icon-subscribe
{ 
	background: url("http://s.ytimg.com/yts/imgbin/www-hitchhiker-vflrAFsIP.png") -2px -192px no-repeat rgba(0, 255, 0, 0.047) !important;
	height: 12px !important;
	width: 12px !important;
}

.yt-uix-button-subscribed-unbranded.hover-enabled:hover .yt-uix-button-icon-subscribe,
.yt-uix-button-subscribed-branded.hover-enabled:hover .yt-uix-button-icon-subscribe
{ 
	background: url("https://s.ytimg.com/yts/imgbin/www-hitchhiker-vflrAFsIP.png") -258px 1px no-repeat transparent !important;
	height: 12px !important;
	width: 12px !important;
}

/*** OLDER ***************************************************/

.category-header .category-title-link:hover .category-title,
.category-header .category-count-link:hover .channel-count,
.category-header .category-link:hover .all-categories
{ 
	color: #1C62B9 !important;
}

.category-header .category-title
{ 
	color: #BEBEBE !important;
}

.category-title-wrap:hover .category-count-link .channel-count
{ 
	color: #BEBEBE !important;
}

.category-header .channel-count
{
	color: #454545 !important;
}

.yt-default h1,
.yt-default h2,
.yt-default h3,
.yt-default h4,
.yt-default h5,
.yt-default h6,
h1.yt,
h2.yt,
h3.yt,
h4.yt,
h5.yt,
h6.yt
{
	color: #FFFFFF !important;
}

.yt-default .yt-notes,
p.yt-notes
{
	color: #BEBEBE !important;
}

.primary-col { 
	background-color: #272727 !important;
	border-bottom: 1px solid #454545 !important;
	border-left: 1px solid #454545 !important;
	border-right: 1px solid #454545 !important;
}

.subscribed-hh-label,
.feed-promo h3,
.feed-item-container:hover .feed-item-time,
.feed-item-content .yt-user-name,
.feed-item-content .metadata .view-count,
#footer-hh-links-primary a,
#footer-links-primary a
{ 
	color: #FFFFFF !important;
}

#watch-later-promo p
{
	color: #BEBEBE !important;
}

#yt-picker-country-footer,
#yt-picker-language-footer,
#yt-picker-safetymode-footer {
	background: #333333 !important;	
}

#footer .yt-picker {
	box-shadow: inset 0px 0px 2px #454545 !important;
}

.yt-picker-content strong
{ 
	color: #FFFFFF !important;
}

#watch7-action-buttons .yt-uix-button-content
{
	color: #AAAAAA !important;
	text-shadow: 0px 1px 0px #000 !important;
}

#savefrom__yt_links, #savefrom__yt_links span[style]
{
	color: #BEBEBE !important;
}

ul.yt-uix-button-menu,
.yt-uix-button-menu
{
	background: #2D2D2D !important;
	border: 1px solid #000000 !important;
}

.yt-uix-button-menu li
{
	color: #FFFFFF !important;
}

.yt-uix-button-menu .yt-uix-button-menu-item
{
	color: #B2B2B2 !important;
}

.watch-playlists-drawer ul
{
	background-image: none !important;
}

#action-panel-addto .playlist-item.selected a,
#action-panel-addto a
{
	color: #B2B2B2 !important;
}

.yt-uix-button-default,
.yt-uix-button-text,
.yt-uix-button-panel .yt-uix-button-text
{
	color: #BEBEBE !important;
	background-color: #2B2B2B !important;
	background-image: none !important;
	border-bottom-color: #454545 !important;
	border-left-color: #454545 !important;
	border-right-color: #454545 !important;
	border-top-color: #454545 !important;
	text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.447) !important;
	box-shadow: inset 0px 0px 2px #454545 !important;
}

.yt-uix-button-default:focus,
.yt-uix-button-default.yt-uix-button-focused,
.yt-uix-button-text:focus,
.yt-uix-button-panel .yt-uix-button-text:focus {
	box-shadow: inset 0px 0px 2px #858585 !important;
}

.yt-uix-button-default,
.yt-uix-button-hh-default,
.yt-uix-button-subscription,
.yt-uix-button-panel:hover .yt-uix-button-text,
.yt-uix-button-panel:hover .yt-uix-button-hh-text,
body .yt-uix-button-default[disabled],
body .yt-uix-button-hh-default[disabled] {
	color: #BEBEBE !important;
	background-color: #2B2B2B !important;
	background-image: none !important;
	border-bottom-color: #454545 !important;
	border-left-color: #454545 !important;
	border-right-color: #454545 !important;
	border-top-color: #454545 !important;
	text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.447) !important;
	box-shadow: inset 0px 0px 2px #454545 !important;
}

.yt-uix-button-default:active,
.yt-uix-button-text:active,
.yt-uix-button-panel .yt-uix-button-text:active,
.yt-uix-button-default.yt-uix-button-active,
.yt-uix-button-default.yt-uix-button-toggled,
.yt-uix-button-text.yt-uix-button-active
{
	background-color: #2D2D2D !important;
	background-image: linear-gradient(to top, #474747 0px, #2B2B2B 100%) !important;
}

#watch7-secondary-actions .yt-uix-button
{
	box-shadow: none !important;
}

.yt-uix-button-icon-wrapper
{
	max-height: 30px !important;
}

#masthead-expanded .yt-uix-slider-prev:focus,
#masthead-expanded .yt-uix-slider-next:focus
{
	background-color: #2B2B2B !important;
	background-image: none !important;
	box-shadow: inset 0px 0px 2px #656565 !important;
}

#masthead-expanded .yt-uix-slider-prev:active,
#masthead-expanded .yt-uix-slider-next:active
{
	background-color: #454545 !important;
	background-image: linear-gradient(to top, #474747 0px, #2B2B2B 100%) !important;
	box-shadow: inset 0px 0px 2px #454545 !important;
}

#branded-page-body-container { 
	background-color: transparent !important;
}

.yt-uix-button:focus .yt-uix-slider-prev-arrow
{
	border-right-color: #999999 !important;
}

.yt-uix-button:focus .yt-uix-slider-next-arrow
{
	border-left-color: #999999 !important;
}

.yt-uix-button:hover .yt-uix-slider-prev-arrow
{
	border-right-color: #DEDEDE !important;
}

.yt-uix-button:hover .yt-uix-slider-next-arrow
{
	border-left-color: #DEDEDE !important;
}

.yt-uix-button-shelf-slider-pager,
.yt-uix-button-shelf-slider-pager:hover
{
	box-shadow: inset 0px 0px 2px #656565 !important;
	background-color: #2B2B2B !important;
}

.feed-item-container .feed-item-main-content .yt-uix-button-shelf-slider-pager:focus
{
	box-shadow: inset 0px 0px 2px #656565 !important;
	background-color: #3B3B3B !important;
}

.branded-page-v2-primary-col
{
	background-color: #222222 !important;
	border-bottom: 1px solid #454545 !important;
	border-left: 1px solid #454545 !important;
	border-right: 1px solid #454545 !important;
}

.branded-page-v2-subnav-container {
	border-bottom: 1px solid #454545 !important;
}

#playlist-editor-title
{
	color: #DEDEDE !important;
}

.epic-nav-dropdown-group:hover,
body a.yt-uix-button-epic-nav-item:hover,
body a.yt-uix-button-epic-nav-item.selected,
body a.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
button.yt-uix-button-epic-nav-item:hover,
button.yt-uix-button-epic-nav-item.selected,
button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item:hover, .epic-nav-item.selected,
.epic-nav-item.yt-uix-button-toggled,
.epic-nav-item-heading
{
	color: #BEBEBE;
}

.playlist-video-item .playlist-video-item-handle
{
	background: #333333;
	border-left: 1px solid #454545;
	border-right: 1px solid #454545;
}

#playlist-info
{
	color: #BEBEBE !important;
}

.playlist-video-item .yt-user-name
{
	color: #BEBEBE !important;
}

.yt-uix-button-icon-c4-editor-edit
{
	background: url("http://hezer.naraku.org/stuff/www-channels-c4-editor-vflYMDsmU-invert.png") 0px -125px no-repeat transparent !important;
}

.yt-uix-button-icon-c4-editor-trash
{
	background: url("http://hezer.naraku.org/stuff/www-channels-c4-editor-vflYMDsmU-invert.png") 0px -81px no-repeat transparent !important;
}

#yt-admin #yt-admin-content
{
	overflow: hidden;
}

#watch7-action-buttons, #watch7-action-panels { 
	border-bottom-color: #454545 !important;
	border-left-color: #454545 !important;
	border-right-color: #454545 !important;
}

#watch7-headline, #watch7-notification-area, #watch7-user-header
{
	border-left-color: #454545 !important;
	border-right-color: #454545 !important;	
}

#watch-discussion
{
	border-bottom: 1px solid #454545;
	border-left: 1px solid #454545;
	border-right: 1px solid #454545;
	border-top: 0px solid #454545;
}

.feed-item-content-wrapper.playlist-promo, .feed-item-content-wrapper.channel-lockup { 
	border: 1px solid #888888 !important;
	box-shadow: 0px 3px 4px #454545 !important;
}

.feed-item-content .metadata
{
	color: #777777;
}

.yt-uix-form-input-checkbox-container .yt-uix-form-input-checkbox-element,
.yt-uix-form-input-radio-container .yt-uix-form-input-radio-element
{
	background-color: #222222 !important;
	box-shadow: inset 0px 0px 2px #454545 !important;
	border: 1px solid #454545 !important;
}

.yt-uix-form-input-checkbox-container:hover .yt-uix-form-input-checkbox-element,
.yt-uix-form-input-radio-container:hover .yt-uix-form-input-radio-element { 
	background-color: #858585 !important;
}

.yt-uix-form-input-checkbox-container input:checked + .yt-uix-form-input-checkbox-element { 
	background: url("http://s.ytimg.com/yts/imgbin/www-hitchhiker-vflrAFsIP.png") -68px -157px no-repeat #222222 !important;
}

.yt-dialog-fg-content,
.yt-uix-overlay-fg-content { 
	color: #BEBEBE !important;
}

.yt-uix-overlay-actions {
	background: #222222 !important;
	border-top: 1px solid #3A3A3A !important;
}

.yt-dialog-base .yt-dialog-header,
.yt-uix-overlay-base .yt-uix-overlay-header { 
	border-bottom: 1px solid #3A3A3A !important;
}

.yt-dialog-base .yt-dialog-header .yt-dialog-close .yt-uix-button-content,
.yt-uix-overlay-base .yt-uix-overlay-header .yt-uix-overlay-close .yt-uix-button-content { 
	background: url("http://s.ytimg.com/yts/imgbin/www-hitchhiker-vflrAFsIP.png") -319px -69px no-repeat transparent;
	height: 10px;
	width: 10px;
}

.primary-col { 
	background:transparent !important;
}

/*---------------------------------------------------*
 * Random coloring and small positioning adjustments *
 *****************************************************/

/*[[resizing]]*/

/************************
 * Playlist Positioning *
 *----------------------*/

.watch7-playlist-bar
{
	width: 100% !important;
}

#playlist
{ 
    width: 100% !important;
}

.watch-medium .watch7-playlist-bar-left,
.watch-large .watch7-playlist-bar-left
{ 
    width: 75% !important;
}

.watch-medium .watch7-playlist-bar-right,
.watch-large .watch7-playlist-bar-right
{ 
	width: 25% !important;
}

.video-list-item > a
{
	width: auto !important;
}

#watch7-playlist-tray .video-list-item a
{ 
    padding: 2px 0px 2px 35px !important;
}

.watch-playlist-collapsed #watch7-playlist-tray-container
{ 
	height: 0px !important;
}

#watch7-playlist-tray,
#watch7-playlist-tray-mask
{
	bottom: 0px !important;
}

/*----------------------*
 * Playlist Positioning *
 ************************/

}
