css:
@-moz-document domain("lifehacker.ru") {
#sidebar, .post-social, .btw, #footer, #topline
{
  display:none !important;
}

#content, #comments
{
  width: 100% !important;
}

#content .post .post-naked
{
  zoom: /*[[zoom]]*/;
  text-align: justify;
  padding-right: 20px;
}

#page, #menu
{
  width: /*[[ScreenWidth]]*/ !important;
}

/*все именения отсюда!!! то что выше не рогать*/

#content .comment-like-inner
{
  right: -10px;
}
 
.post-cat
{
  
  position: absolute;
  right: 40px !important;
}

#page {
  padding: 0;
  border-right: solid 1px #ccc;
  border-left: solid 1px #ccc;
}

input.inset, #content .add-body-textarea {
  box-shadow: none !important;
}

#header #manage {
  padding: 30px 15px 0 15px;
  margin-top: 0px;
}

#header #logo {
  top: 5px;
  margin-top: 0px;
  zoom: 80%;
}

input.buttonc {
  text-shadow: none;
  color: #222 !important;
  background: #f8f8ec;
  border-radius: 0;
  -webkit-border-radius: 0px;
  border: 1px solid #ccc;
  font-size: 1.1em;
  font-weight: bold;
}

#content .add {
  margin-bottom: 10px;
}
}
