@charset "utf-8";
@-moz-document url-prefix("about:newtab") {
   html , 
   body , 
   div { background: #0F0D00 url(/*[[img-tab]]*/) no-repeat center fixed !important; background-size: cover !important; }
}
@-moz-document url("about:Home"), url("about:home") {

#brandLogo, #snippetContainer, #searchEngineLogo, #restorePreviousSessionSeparator{ display: none !important; }

html { background: #0F0D00 url(/*[[img-home]]*/) no-repeat center fixed !important; background-size: cover !important; }

body { background: transparent !important; }
}
