css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.baidu.com") {

.s-weather-border{
    border-bottom: none !important;
}
.bottom-line1,.bottom-line2,#s_seth{
    display: none !important;
}
.s-skin-user{
    background: url(/*[[bgimg]]*/) no-repeat center fixed;
    background-size: 100% auto;
}
#lg img{
    width: 0 !important;
}
#lg{
    background: url(/*[[logo]]*/) no-repeat center !important;
}
#kw{
    background: transparent !important;
    background-color: /*[[inputbg]]*/ !important;
    border-color: /*[[inputcolor]]*/ !important;
    -moz-transition: background-color .5s ease-in-out;
    -webkit-transition: background-color .5s ease-in-out;
}
#kw:focus{
    /*[[kw]]*/
    background-color: /*[[inputbgf]]*/ !important;
    -moz-transition: background-color .5s ease-in-out;
    -webkit-transition: background-color .5s ease-in-out;
}
.btn_wr{
    background: none !important;
}
.btn{
    /*[[btcolor]]*/
    width: 80px !important;
    height: 28px !important;
    margin-top: 2px !important;
    background: -moz-linear-gradient(left,rgba(255,255,255,.2),rgba(255,255,255,.4),rgba(255,255,255,.2)) !important; 
}
.btn:hover{
    background: -moz-linear-gradient(left,rgba(255,255,255,.3),rgba(255,255,255,.5),rgba(255,255,255,.3)) !important;
}
.s-title .s-title-bg,.s-content{
    background: transparent !important;
}
.mod-sc.s-mod{
    border: none !important;
    background-color: /*[[opacity]]*/ !important;
    -moz-transition: background-color .5s ease-in-out,box-shadow .5s ease-in-out;
    -webkit-transition: background-color .5s ease-in-out,box-shadow .5s ease-in-out;
}
.mod-sc.s-mod:hover{
    background-color: /*[[hopacity]]*/ !important;
    box-shadow: 2px 2px 2px 1px rgba(0,0,0,.1) !important;
    -moz-transition: background-color .5s ease-in-out,box-shadow .5s ease-in-out;
    -webkit-transition: background-color .5s ease-in-out,box-shadow .5s ease-in-out;
}
.s-nplus *{
    border: none !important;
}
/*[[inputfocus]]*/
/*[[linkcolor]]*/
.s-mod-nav,.s-ncf-name-link{
    color: #42474A !important;
}
.s-nav-name img{
    display: none !important;
}
.mod-sc.s-mod:hover .s-title:before{
    content: url(/*[[moe]]*/) !important;
}
/*[[linkhover]]*/
.user-name-top{
    background: none !important;
}
.s-username-menu,.s-username-menu a.sep{
    border-color: rgba(255,255,255,.3) !important;
}
.s-username-menu a{
    background-color: rgba(255,255,255,.4) !important;
    -moz-transition: background-color .5s ease-in-out;
    -webkit-transition: background-color .5s ease-in-out;
}
.s-username-menu a:hover{
    background-color: rgba(255,255,255,.6) !important;
    -moz-transition: background-color .5s ease-in-out;
    -webkit-transition: background-color .5s ease-in-out;
}
#st{
    background-color: rgba(255,255,255,.4) !important;
}
div[id^="sd_"]{
    border-color: rgba(255,255,255,.4) !important;
}
#st tr{
    background-color: rgba(255,255,255,.4) !important;
    -moz-transition: background-color .5s ease-in-out;
    -webkit-transition: background-color .5s ease-in-out;
}
#st tr:hover{
    background-color: rgba(255,255,255,.6) !important;
    -moz-transition: background-color .5s ease-in-out;
    -webkit-transition: background-color .5s ease-in-out;
}
#st tr b{
    -moz-transition: color .5s ease-in-out;
    -webkit-transition: color .5s ease-in-out;
}
#st tr:hover b{
    color: red !important;
    -moz-transition: color .5s ease-in-out;
    -webkit-transition: color .5s ease-in-out;
}
/*[[layout]]*/
