css:
/* AGENT_SHEET */
scrollbarbutton{display: none !important;}
scrollbar {
    -moz-appearance: none !important;
    -moz-transition-duration: 1s;-moz-transition-delay: .1s;
    background: none !important;
    opacity: 0 !important;
    z-index: 999 !important;
    }
scrollbar thumb {
    -moz-appearance: none !important;
    background:/*[[mycolor]]*/ !important;
    border: 0 !important;
    border-radius: 5px !important;
    }
scrollbar:hover{opacity: .5 !important;}
scrollbar:active{opacity: .7 !important;}
scrollbar[orient="vertical"] thumb{min-height: 100px !important;}
scrollbar[orient="vertical"] {
    width: 8px !important;
    margin:30px 2px 10px -10px !important;
    }
scrollbar[orient="horizontal"] {
    height: 8px !important;
    margin: -10px 10px 2px 5px !important;
    }
:not([id="ldt pre"])>scrollbar{position: relative !important;}
