css:
@-moz-document domain("newsblur.com") {
/* Disables wrapping of story titles in split view */
.NB-story-layout-split #story_titles .NB-story-title a.story_title {
	white-space: nowrap !important;
}

/* Hides story date and removes empty line in split view */
.NB-story-layout-split .NB-story-titles .story_date.NB-hidden-fade {
	visibility: hidden !important;
	position: absolute !important;
}
}
