css:
@-moz-document domain("neogaf.com") {
blockquote.quote[data-username="Jzero"] {
background: #C47021 !important;
border-color: #CC782A !important;
color: #FFFFFF; 
}

.thead, .tfoot {
background-color: #302E2E !important;
border-color: #545151 !important;
color: #ffffff !important;

}

.forum-tabs.bot {
border-color: #3D3B3B !important;
}

.large-button { 
background-color: #302E2E !important;

}

.large-button:hover { 
background-color: #191919 !important;

}
.large-button.submit, a.large-button.submit {
background-color: #505050;
border-color: #302E2E;
color: #FFA229;
}

.large-button.submit:hover, a.large-button.submit:hover {
background-color: #191919;
border-color: #191919;
color: #DB6810;
}

#main, #footer, .goodie728 {
background-color: #545151 !important;
}

#header
{
background-color: #333333 !important;
}

.tcat, #threadsearch.wmenu, #forumsearch.wmenu, .pagenav > li.current > span {
background: #302E2E !important;
border-color: #545151 !important;
}

.pagenav > li.current > span {
background: #333333 !important;
}

#toolbar {
background-color: #545151 !important;
}

.forum-tabs > li > a {
display: block;
line-height: 23px;
font-weight: 700;
font-size: 12px;
color: #fff;
background-color: #4982ae;
border-radius: 3px 3px 0 0;
padding: 0 12px;
position: static;
  margin-right: -10px !important;
}

.forum-tabs > li.activetab > a {
				background-color: #2D2D2D !important;
				z-index: 2;
				line-height: 24px;
			}

.forum-tabs > li > a {
color: #ccc;
background-color: #414141;
}

.panelsurround {
background-color: #3b3d40 !important;
}

.panel {
background-color: #3D3B3B !important;
border-color: #3c414d !important;
}


.alt1, .alt1Active, .alt2, .alt2Active {
background: #3D3B3B !important;
border-color: #545151 !important;
color: #B2B2B2 !important;
}

.alt1 .postbit-details, .alt1 .postbit-post, .alt1 .post-meta-border, .alt1 blockquote.quote {
border-color: #545151;
}

.alt2 {
background: #3D3B3B !important;
border-color: #545151 !important;
}

.quote {
background: #4C4A4A !important;
border-color: #545252 !important;

}

.postbit-details, .threadbit > td {
border-color: #545151 !important;
color: #BFBFBF !important;
}


#splash {
color: #ffffff !important;
}

#forum-list {
background-color: #333333 !important;
}

#forum-extras {
background-color: #444444 !important;
}

.goodie300 {
background-color: #333333 !important;
}

.forum-bit-lastpost {
border-left: 1px solid #ffffff !important;
}

[class^="forumextraicon-"], .forum-info > .forumicon {
background-color: #999999 !important;

}


.spoiler {
color: #000000 !important;
background: #000000 !important;

}

.spoiler.show {
color: #ffffff !important;
background: #000000 !important;

}

a:link, a:active {
color: #ffa216 !important;
}

a:hover {
color: #db7700 !important;
}

a:visited {
color:#DBCFC0 !important;
}

#links a:hover {
color: #db7700 !important;
}

.post-meta-border, .mypost blockquote.quote {
border-color: #545151 !important;
}

.vBulletin_editor {
	background: #3B3D40;
	padding: 8px;
}


html, body {
color: #ffffff  !important;
background-color: #545151 !important;
}

.forum-stats-bits > li > var {
display: block;
text-align: center;
font-style: normal;
font-size: 20px;
line-height: 32px;
color: #999999;
}

.stats-whatsgoingon h4 {
font-size: 20px;
line-height: 32px;
margin: 0;
color: #999999;
}

.vbmenu_control {
background: #302E2E;
color: #FFFFFF;
font-weight: bold;
padding: 4px 8px;
white-space: nowrap;
}

.mypost, .mypost .postbit-post, .mypost .postbit-details{
background-color:#302E2E !important; 
border-color: #545151 !important;  
}

  
.mypost blockquote.quote{
border-color: #545151 !important; 
}



.footer-links {
color: #878787;
}

.newreplybox p {
color: #A7B2B2 !important ;
margin: 0 0 3px;
text-align: center;
}



.forum-tabs > li > a:before {

border-color: transparent transparent transparent ;
position: fixed;
				
			}

.forum-tabs > li > a:after {				
border-color: transparent transparent transparent ;
position: fixed;
				}

.vBulletin_editor {
border: 1px solid #1e1e1e !important;
background-color: #3D3B3B !important;
}

.quote.code {
  background: #333 !important;
  color: #fff !important;
}

}
