/* RSS Feeds ________________________________________________________________________*/

selector {
	background-image: linear-gradient(to bottom right, #f99500, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

/* Tumblr ________________________________________________________________________*/

selector {
	background-image: linear-gradient(to bottom right, #254e7a, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #365f8b, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

