@-moz-document domain("bing.com") {
/* Test */

#rfCarousel .item {
    width: auto;
    margin-right: 8px;
}

x#rfCarousel .carousel-controls,
x#rfPane .touchQuery,
x#rfPaneIn,
xrfPane,
x.carousel
{
    height: auto!important;
}

.carousel-content {
	background-color: #1f1f1f!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.touchQuery .carousel-scroll-wrapper .items>li {
    height: 48px;
	margin: 12px;
    border: none;
}

.carousel-content {
	padding: 12px;
}

/* Image layout _____________________________________________________________________________________________*/

*[data-layout="row"].dgControl>ul
{
	white-space: pre-line!important;
}

x#mmComponent_images_2>ul .iuscp,
x.iuscp.varh.gencell.inline_blk
 {
    width: 98%!important;
}

.iusc {
    width: 100%!important;
    margin-right: auto!important;
    margin-left: auto!important;
}

.carousel .carousel-scroll-content,
.carousel .carousel-scroll-wrapper,
.iuscp.varh.gencell.inline_blk
{
    height: auto!important;
}

x.carousel .items {
    border-collapse: separate!important;
    border-spacing: 12px!important;
    white-space: pre-line!important;
}

.img_info, .img_badges {/* Font size of dimensions & type */
    font-size: 15px;
}

.img_cont { /* Move thumbnail down */
    top: 2rem;
    border-radius: 6px 6px 0 0;
}

.img_info.hon { /* Move dimensions & type */
    top: -1rem!important;
}

.img_info.hon>*:first-child {
    margin-top: -9px;
}

.img_info.hon>* {
    box-sizing: border-box;
    display: block;
    margin-bottom: 22rem!important;
    overflow: visible;
    padding-left: 0;
    padding-right: 0;
    text-overflow: ellipsis;
}

.dg_b .infnmpt {
    padding: 0 8px;
    margin-top: 2rem;
}

.dg_b .infnmpt { /* Move thumbnail text down */
    margin-top: -7rem;
}

.dg_b .infnmpt .infpd a { /* Move thumbnail down */
    display: inline-block;
    font-size: 18px;
    line-height: 20px;
    max-height: none;
    width: 89%!important;
}

x#mmComponent_images_2>ul>li {
	background-color: #1f1f1f!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}















