css:
@-moz-document domain("theoldreader.com") {
	.container-fluid .row-fluid .span3 {
		width: /*[[width]]*/;
	}

	.container-fluid .row-fluid .sidebar-fixed-top {
		position: relative;
	}

	.container-fluid .row-fluid .span9 {
		float: none;
		position: static;
		width: auto;
		padding-left: 10px;
	}

	.container-fluid .row-fluid .span9 .slide {
		margin-left: /*[[width]]*/;
		padding-left: 10px;
		padding-right: 10px;
	}

	.body-fixed-top>.static {
		margin-right: 10px;
	}
}
