css:
@-moz-document url-prefix("http://dubtopian-review.eu/") {

/*Mod Color*/
div.wpmudev-chat-row.wpmudev-chat-row-moderator a.wpmudev-chat-user.wpmudev-chat-user-name {
    color: /*[[clrMod]]*/ !important;
}


/* Regular member Color */
div.wpmudev-chat-row.wpmudev-chat-row-user a.wpmudev-chat-user.wpmudev-chat-user-name {
    color: /*[[clrReg]]*/ !important;
}



/*Time box color */

div.wpmudev-chat-row span{
background-color: /*[[clrTbox]]*/ !important;
color: /*[[clrTtxt]]*/ !important;
}


/* chat border color */
div.wpmudev-chat-module.wpmudev-chat-module-users-list.wpmudev-chat-users-list-position-right.wpmudev-chat-users-list-show-name {
    border-color: /*[[borderclr]]*/ !important;
}


ul.wpmudev-chat-send-meta {
    border-color: /*[[borderclr]]*/ !important;
}

div.wpmudev-chat-module.wpmudev-chat-module-header {
    border-color: /*[[borderclr]]*/ !important;
}

div.wpmudev-chat-box {
    border-color: /*[[borderclr]]*/ !important;
}



}
