css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix("https://www.tumblr.com")

, url-prefix("https://www.tumblr.com/") {

.post_full .post_permalink:before
{
content: none !important;
}
  
.identity-refresh .post_full .post_permalink
{
border-style: none !important;
}

.post_full:hover  .post_permalink 
{
top: -3px !important;
width: 25px !important;
height: 25px !important;
overflow: visible !important;
right: -3px !important;
opacity: 1 !important;
background: url("/*[[panties1]]*/") no-repeat scroll 0% 0% transparent !important;
}

.post_full .post_permalink:hover
{
opacity: 1 !important;
background: url("/*[[panties2]]*/") no-repeat scroll 0% 0% transparent !important;
}

.post_full:hover
{
border-radius: 6px 26px 3px 6px !important;
}

#posts.posts_v2 .new_post_buttons:hover 
{
border-radius: 6px 6px 6px 6px !important;
}

#posts.posts .new_post_buttons 
{
border-radius: 6px 6px 6px 6px !important;
}
}
