css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home") {html, #launcher, .spacer, #searchContainer, #snippetContainer, #topSection{background:#222222 url("/*[[image]]*/") fixed no-repeat right top!important; background-size:100% 100%!important;
border:transparent!important;
box-shadow:none!important;
border-top: none !important;}

#launcher,
#searchEngineLogo,
#flame-wrap, 
#snippets,
#brandLogo
{display:none!important;}

#searchText {padding:1px 8px!important;
font-size: 16px !important;
color: white !important;
text-shadow: 1px 1px 2px #fff !important; 
    background: none repeat scroll 0% 0% padding-box rgba(10,10,10,0.5)!important;
    border-width: 0px!important;}


#searchText:hover {padding:1px 8px!important;
font-size: 16px !important;
color: white !important;
text-shadow: 1px 1px 2px #fff !important; 
    background: none repeat scroll 0% 0% padding-box rgba(20,20,20,0.7)!important;
    border-width: 0px!important;}

#searchSubmit {
border:none!important;
    background-image: linear-gradient(rgb(100, 100, 100), rgb(5, 5, 5))!important;}

#searchSubmit:hover {
color: grey !important;
text-shadow: 1px 1px 2px grey !important;
border:none!important;
    background-image: linear-gradient(rgb(5, 5, 5), rgb(100, 100, 100))!important;}}
