css:
@-moz-document url-prefix("https://www.instagram.com/") {
* {
    border: none !important;
}

main {
    background-color: black !important;
}

/* username front*/
main > section > div > div > div > div > div > a {
    color: white !important;
}

/* userpage info*/
header h1, header span, header button, header li {
    color: white !important;
}

.Nd_Rl, .fx7hk {
    background-color: white;
    margin-bottom: 3%
}

#react-root > section > main > div > header > section > div > div > button {
	background-color: white !important;
    border-radius: 50%;
}

header > div > div > div {
	overflow: visible !important;
}    

/* userpage info*/
.felixSpriteProfileChannelNullState, .coreSpriteSaveNull, .coreSpriteTaggedNull {
    background-color: white;
    border-radius: 50%;
}

main > div > div > div > div > div > div, main > div > div > article > div > div > div > div {
    color: white !important;
}

.felixSpriteProfileChannelNullState > div > div > div {
    color: white !important;
}

h1, h2, ._6auzh, main > div > article > div > div > p, .uL8Hv {
    color: white !important;
}

#react-root > section > main > div > div > article > header > div.o-MQd.z8cbW > div.PQo_0.RqtMr > div.bY2yH > button {
	color: #0000FF !important;
}

/* Instagram blue links/buttons*/
.y3zKF:not(.yWX7d), a.y3zKF:not(.yWX7d), a.y3zKF:not(.yWX7d):visited, main > div > header > section > div > div > span > span > button {
    background-color: #0000FF !important;
}

.yWX7d, a.yWX7d, .hUQXy, a.tdiEy, .gtFbE, #react-root > section > main > article > div > div > p > a, header a, header a:visited {
    color: #0000FF;
}
    

/*footer*/
footer a, footer span {
    color: black !important;
}
}
