css:
@-moz-document url("https://www.facebook.com/"), url-prefix("https://www.facebook.com/index.php?stype=lo"), url-prefix("https://www.facebook.com/index.php") {

body.fbIndex {
    background: #000000 url('/*[[image]]*/') repeat fixed !important;
}

.rfloat {
    float: none !important;
}

.loggedout_menubar {
    background-color: /*[[barbg]]*/ !important;
    width: 566px !important;
    padding: 10px !important;
}

.loggedout_menubar_container {
    background: url('http://i44.tinypic.com/2lc4bwn.png') !important;
    opacity: .75 !important;
    -webkit-transition: all 0.6s ease !important;
    -moz-transition: all 0.6s ease !important;
    -o-transition: all 0.6s ease !important;
}

.loggedout_menubar_container:hover {
    opacity: .85 !important;
    -webkit-transition: all 0.6s ease !important;
    -moz-transition: all 0.6s ease !important;
    -o-transition: all 0.6s ease !important;

}

._7d, ._74, .pvl, #pageFooter, ul.uiList.localeSelectorList._509-._4ki._6-h._6-j._6-i {
    display: none !important;
}

.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter { display: none !important }

#blueBarHolder {
    display: table !important;
    height: 75% !important;
    position: absolute !important;
    overflow: hidden !important;
    width: 100% !important;
}

#blueBar {
    display: table-cell !important;
    vertical-align: middle !important;
}

.sp_2xp1ac {
    background-image: url('http://i43.tinypic.com/ws7plt.png') !important;
}

.sx_f72f28 {
    width: 175px !important;
    background-position: 0 0px !important;
}

.uiButtonConfirm {
    background-image: none !important;
    background-color: /*[[button]]*/ !important;
    border-color: /*[[barbg]]*/ !important;
}

a.fbIndex, label.fbIndex {
    color: #fff !important;
}

.inputtext {
    border-color: /*[[barbg]]*/ !important;
}

}
