css:
@-moz-document domain("bungie.net") {

.nav_top .nav_logo .dropdown:not(.nodark),
.Nav_Top .nav_identity .dropdown:not(.nodark){
    background: #E4E5E7 ! important;
}

.container_textfield:not(.nodark),
.dualColList:not(.nodark),
.groups_search_section:not(.nodark),
.activity_search_section:not(.nodark),
.news_search_section:not(.nodark),
.topics_search_section:not(.nodark){
    background: #ebebeb ! important;
}

.Community .container_sections:not(.nodark),
.Bungie .container_textblock > div:not(.nodark),
.Bungie .container_textblock h3:not(.nodark){
    background: none ! important;
}

/*[[mycolumns]]*/

.topics li:not(.nodark):hover{
    background: #d2d2d2 ! important;
    box-shadow: 4px 4px 60px #ffffff inset ! important;
}

.topics .followedAuthor:not(.nodark){
    box-shadow: 4px 0 0 #0096DB inset ! important;
}

.topics .followedAuthor:not(.nodark):hover{
    background: #d2d2d2 ! important;
    box-shadow: 4px 4px 60px #0096DB inset ! important;
}

#roster_list .section ul li:not(.nodark),
#followed_list .section ul li:not(.nodark),
.Nav_Top .section > li:not(.nodark),
.pinned .section > li:not(.nodark){
    border-bottom: 1px solid #DBDBDB ! important;
    border-top: none ! important;
    box-shadow: none ! important;
}

#roster_list h5:not(.nodark),
#followed_list h5:not(.nodark){
    background-color: #f2f2f2 ! important;
    box-shadow: none ! important;
}

#recipients ul:not(.nodark),
.Nav_Top .container_section:not(.nodark),
.Nav_Top .link:not(.nodark),
.Nav_Top .panel:not(.nodark){
    background-color: #e0e0e0 ! important;
}

.Nav_Top .link:not(.nodark):hover{
    background-color: #eeeeee ! important;
}

#recipients ul:not(.nodark),
#recipients:not(.nodark){
    background-color: #e0e0e0 ! important;
    box-shadow: none ! important;
    border: none ! important;
}

.Nav_Top .container_textarea:not(.nodark){
    border: none ! important;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.1) inset ! important;
}

#mail_create .btn_sendMessage:not(.nodark),
#mail_conversation .btn_reply:not(.nodark){
    background: -moz-linear-gradient(center top , #459BDD, #358BD6) repeat scroll 0 0 transparent ! important;
    background: -webkit-linear-gradient(center top , #459BDD, #358BD6) repeat scroll 0 0 transparent ! important;
    background: linear-gradient(center top , #459BDD, #358BD6) repeat scroll 0 0 transparent ! important;
}

.Nav_Top #mail_conversation article .avatar.only:not(.nodark){
    height: 40px ! important;
    margin-left: 5px ! important;
    margin-top: 5px ! important;
    width: 40px ! important;
}

#mail_conversation .me time:not(.nodark),
.pinned .me time:not(.nodark){
    float: left ! important;
    padding-left: 43px ! important;
}

#mail_conversation article time:not(.nodark){
    float: left ! important;
    padding-left: 0px ! important;
}

.Nav_Top .me .con_list p:not(.nodark){
    font-style: normal ! important;
}

.Nav_Top article p:not(.nodark),
.pinned article p:not(.nodark){
    max-width: 360px ! important;
}

#mail_conversation .me .content p:not(.nodark),
.pinned .me .content p:not(.nodark){
    text-align: left ! important;
    left: 43px ! important;
}

#mail_conversation .me .container_avatar:not(.nodark),
.pinned .me .container_avatar:not(.nodark){
    left: 0 ! important;
    right: auto ! important;
}

.Nav_Top .mail article .avatar:not(.nodark),
.pinned .mail .avatar:not(.nodark){
    float: left ! important;
    height: 23px ! important;
    margin: -3px ! important;
    position: relative ! important;
    width: 23px ! important;
}

.Nav_Top article .avatar.only:not(.nodark),
.Nav_Top article .avatar:not(.nodark),
.Nav_Top .mail article .container_avatar:not(.nodark),
.pinned .avatar:not(.nodark),
#followed_list .container_avatar:not(.nodark),
.Nav_Top article .avatar:not(.nodark){
    height: 50px ! important;
    padding-left: 7px ! important;
    padding-top: 6px ! important;
    width: 50px ! important;
}

.Nav_Top #mail_conversation article .avatar.only:not(.nodark){
    height: 40px ! important;
    margin-left: 0 ! important;
    margin-top: 0 ! important;
    width: 40px ! important;
    padding-left: 0px ! important;
    padding-top: 0px ! important;
}

.avatar:not(.nodark){
    overflow: inherit ! important;
}

#mail_conversation .me:not(.nodark){
    background: none repeat scroll 0 0 #F1F1F1;
    box-shadow: 0 1px 0 0 #B4B4B4 inset;
}

/*[[mysearch]]*/

.container_sort:not(.nodark){
    background: none repeat scroll 0 0 #E4E5E7 ! important;
    box-shadow: 1px 1px 0 #EAEAEA inset ! important;
}

.forumPost .header_post:not(.nodark),
#post > .post:not(.nodark){
    background: rgba(245, 245, 245, 0.3) ! important;
}

#post > .post:not(.nodark){
    border: none ! important;
}

.topics li:not(.nodark),
.forumPost .post:not(.nodark){
    border-top: 1px solid #DBDBDB ! important;
}

.forumPost .post_info:not(.nodark){
    border-bottom: 1px solid #DBDBDB ! important;
}

.pager_prevnext:not(.nodark){
    border-bottom: 1px solid #DBDBDB ! important;
    border-top: 1px solid #DBDBDB ! important;
    box-shadow: none ! important;
}

.topics .avatar:not(.nodark):before,
.forumPost article .avatar:not(.nodark):before{
    border: none ! important;
    box-shadow: none ! important;
}

.forumPost article .avatar:not(.nodark){
    z-index: 2 !important;
}

/*[[myavatar]]*/

/*[[myborders]]*/

.forumPost .arrow a:not(.nodark){
    background-image: url("http://i.imgur.com/6Y6QAZ5.png") ! important;
}

.forumPost .arrow:not(.nodark){
    border-color: #DBDBDB ! important;
}

/*[[mytext]]*/

/*[[mymute]]*/

/*[[mysocial]]*/

/*[[mytitles]]*/

.forumPost .postSortMode a:not(.nodark){
    min-width: 50px ! important;
}

.Community .header_body h2:not(.nodark){
    font-size: 20px ! important;
}

.container_sort .sort a:not(.nodark){
    line-height: 21px ! important;
}

.topics .author:not(.nodark),
.topics .author span:not(.nodark),
.topics .extraInfo:not(.nodark),
.topics .extraInfo a:not(.nodark){
    color: #777777 ! important;
}

.topics .extraInfo:not(.nodark),
.topics .extraInfo a:not(.nodark){
    text-shadow: none ! important;
}

/*[[mysiteborder]]*/

/*[[mybackground]]*/

/*This section is for my personal use*/

a[data-membershipid="334211"]:not(.nodark):hover {
    color: #000000 ! important;
    text-shadow: none ! important;
}

h1 > a[data-membershipid="334211"]:not(.nodark):after,
div.authorInfo > a[data-membershipid="334211"]:not(.nodark):after{
    background: none repeat scroll 0 0 #D1D1D1 ! important;
    border: 1px inset #000000 ! important;
    color: #000000 ! important;
    text-shadow: 0 1px 1px #ffffff ! important;
    content: "Theme Author" ! important;
    display: inline-block ! important;
    font-family: 'OpenSansSemiBold',Arial,sans-serif ! important;
    font-size: 9px ! important;
    line-height: 13px ! important;
    margin-left: 5px ! important;
    padding: 0 2px ! important;
    position: relative ! important;
    text-transform: none ! important;
    top: -1px ! important;
    vertical-align: middle ! important;
}

.forumPost article .avatar[href$="mid=334211"]:not(.nodark):before,
.topics article .avatar[href$="mid=334211"]:not(.nodark):before{
    background: url("http://i.imgur.com/U9v8nYN.png") ! important;
    background-size: 100% ! important;
    border: none ! important;
    border-radius: 0px ! important;
    bottom: 0 ! important;
    box-shadow: 0 1px 2px -1px rgba(0, 0, 0, 0.4) inset ! important;
    content: "" ! important;
    left: 0 ! important;
    position: absolute ! important;
    right: 0 ! important;
    top: 0 ! important;
}

}

@-moz-document
url-prefix("http://www.bungie.net/7_viltre/en/view/profile/index#!page=index&mid=334211"),
url-prefix("https://www.bungie.net/7_viltre/en/view/profile/index#!page=index&mid=334211"),
url-prefix("http://admin.bungie.net/7_viltre/en/view/profile/index#!page=index&mid=334211"),
url-prefix("https://admin.bungie.net/7_viltre/en/view/profile/index#!page=index&mid=334211"){

body.loaded .header_page:not(.nodark){
    opacity: 1 ! important;
}

.MyCommunity .header_page:not(.nodark),
.Edit .header_page:not(.nodark){
    background: url("http://i.imgur.com/8SC0gVj.png") no-repeat scroll center top transparent ! important;
}

}
