css:
@-moz-document domain("tieba.baidu.com") {
	/* 右上角菜单 */
	.userbar .u_member {
		display: none !important;
	}
	/* 搜索框无关内容 */
	.suggestion_list .break_title,
	.suggestion_list .operation_item {
		display: none !important;
	}
	/* 广告头图 */
	.plat_recom_carousel {
		background: transparent;
		height: 100px !important;
	}
	.plat_recom_carousel > * {
		display: none !important;
	}
	/* 吧信息处各种没用的东西 */
	.top_content,
	.plat_head_v2_unmain_wrapper {
		display: none !important;
	}
	/* 列表页小徽章 */
	.icon_wrap,
	.pre_icon_wrap {
		display: none !important;
	}
	/* 内容页 徽章 */
	.achievement_medal_section,
	.d_nameplate {
		display: none !important;
	}
	/* 红色标题 */
	.member_thread_title_pb {
		color: #000 !important;
	}
	/* 侧边栏广告 */
	.topic_list_box,
	.search_back_box,
	.nani_app_download_box,
	.app_download_box,
	.my_app,
	.celebrity {
		display: none !important;
	}
	/* 多余的几个浮动按钮 */
	.tbui_aside_float_bar .tbui_fbar_down,
	.tbui_aside_float_bar .tbui_fbar_lab {
		display: none !important;
	}
	/* 底部横幅 */
	.xiu8_follow_warn {
		display: none !important;
	}
}
