css:
@-moz-document domain("osu.ppy.sh") {
#coolheader, .footer{
  display:none!important;
}


.mainbody2 {
  background-image: none!important;
}
#playstats ,#tablist a{
  background: none!important;
}
.sf-menu li:hover,.sf-menu li.sfHover,.sf-menu a:focus,.sf-menu a:hover,.sf-menu a:active{
  background:rgba(243,117,197,0.5);color:#fff;outline:0!important;
}
#tablist,#songinfo {
  border-bottom: none!important;
  background: rgba(230,226,240,0.4)!important;
  border-radius:7px!important;
}


.avatar-holder {
  background:rgba(255,255,255,0.4)!important;
  box-shadow: none!important;
  border: none!important;
}
.posttext {
  background: rgba(255,255,255,0.6)!important;
  border: none!important;
}


#tablist A.active:link, #tablist A.active:visited {
  background: #cab8f7!important;
  border-radius:7px;
}

.prof-beatmap{
background: rgba(231,228,252,0.2)!important;
}
.prof-beatmap div:hover{
background: rgba(231,228,252,0.8)!important;
}

#songinfo td {
  border-bottom: none!important;
}
.content-with-bg {
  background-color: rgba(240,236,250,0.4)!important;
  background-image: none!important;
  border-radius:7px!important;
  padding: 1px 2px 1px 2px;
  min-height: 400px;
}
.beatmapListing th {
  background-color: rgba(240,236,250,0.1);
  border-bottom:none!important;
}
.final_footer{
  background-image: none!important;
}
.sectionContents {
  background-color: rgba(255,255,255,0.4)!important;
  border-radius:7px;
}
.prof-right .userpage,.userpage-expand,.profile-tabs ,.profileStatHeader,.profileStatLine ,.beatmap,.playstyle-container, #songinfo td.colour{
  background-color: rgba(240,236,250,0.4)!important;
  border-radius:7px;
}
.row1p {
  background-color: rgba(240,236,250,0.4)!important;
}
.row2p {
  background-color: rgba(230,226,240,0.4)!important;
}
.bodytopbg {
  background: #a9a9ff url("/*[[img]]*/") repeat!important;
  min-height: 700px!important;
}
}
