/* Removing Img Google Logo */
div.logo a#logo img {
    display: none !important;
}

/* Logo Google - Search, Images Tab */
div.logo {
    margin-left: 0px !important;
    margin-top: 0px !important;
    
    padding-left: 0px !important;
    padding-right: 0px !important;
    
    top: -14px !important;
    left: -170px !important;
}

/* Logo Google - Videos Tab  */
div#logocont {
    margin-left: 0px !important;
    margin-top: 0px !important;
    
    padding-left: 0px !important;
    padding-right: 0px !important;
    
    top: -18px !important;
    left: 20px !important;
}

/* Adding our own Doodle */
a#logo {
    width: 159px !important;
    height: 60px !important;
    
    max-width: 159px !important;
    min-width: 159px !important;
  
    max-height: 60px !important;
    min-height: 60px !important;
    
    background-image: /*[[logoUserChoice]]*/ !important;
    background-position: 0px 0px !important;
    background-size: 159px 60px !important;
    background-repeat: no-repeat !important;
    
    margin-left: 0px !important;
    margin-top: 0px !important;
}

/* Search form adjustment (Videos) */
div#searchform.jsrp.mdm form#tsf.tsf.nj > div > div.sfibbbc {
    margin-left: 70px;
}

/* Search form adjustment (All + Images) */
div#searchform.jsrp.big form#tsf.tsf.nj > div > div.A8SBwf,
div#searchform.jsrp.mdm form#tsf.tsf.nj > div > div.A8SBwf { 
    margin-left: 190px; 
}

/* Google Default Doodle */
div#logocont a > img,
div#searchform.jsrp.mdm form#tsf.tsf.nj > div > div.A8SBwf > div.logo a > img /* This if for Google Images */ {
    width: 159px !important;
    height: 60px !important;
     
    max-width: 159px !important;
    min-width: 159px !important;
  
    max-height: 60px !important;
    min-height: 60px !important;
    
    opacity: 0 !important;
    
    margin-left: 0px !important;
    margin-top: 0px !important;
}

/* Logo Google - Main Page */
div#lga > img#hplogo {
    width: 0px !important; /* Very naughty fix :) */
    height: 0px !important;
	
    padding-top: 174px !important; 
    padding-left: 450px !important;
    
    background: /*[[logoUserChoice]]*/ no-repeat center !important; 
    background-size: 450px 174px !important;
    background-repeat: no-repeat !important;
    
    position: relative !important;    
    overflow: visible !important;
    
    top: 39px !important;
}

/* Logo Google - Main Page (Images) */
div#lga > div > div#hplogo {
    
    width: 450px !important;
    height: 174px !important;
        
    background: /*[[logoUserChoice]]*/ no-repeat center !important; 
    background-size: 450px 174px !important;
    background-repeat: no-repeat !important;
        
    position: relative !important;    
    overflow: visible !important;
    
    top: -70px !important;
}

/* Signs "Russia", "Images" etc. - Main Page */
div#lga div #hplogo div {

    top: 80px !important;     
    left: 445px !important;

    float: left !important;
}

/* Mobile version */
img#mdlogo {
    background: /*[[logoUserChoice]]*/ no-repeat center !important;
    background-size: 180px 70px !important;
    
    width: 180px !important;
    height: 70px !important;
    padding: 180px 70px 0 0;
    
    top: -60px;
    
    position: relative !important;
} 

header#hdr > div > a > svg {
    background: /*[[logoUserChoice]]*/ no-repeat center !important;
    background-size: 180px 70px !important;
    
    width: 0px !important;
    height: 0px !important;
    
    padding-top: 70px !important;
    padding-left: 180px !important;
    
    top: -18px;
    position: relative !important;
}
}
