css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.baidu.com/s?"),url-prefix("http://www.baidu.com/baidu?"),url-prefix("http://www.baidu.com/#"){
/*[[UI]]*/
/*[[firefox]]*/
a:link,em,font,span,div{
    text-decoration:none!important}
#container{
    margin-top:42px;
    padding-left:20px!important}
#content_right{
    position:absolute;
    right:25px;
    border-left-color:transparent!important}
#con-ar,#con-ar+.result-op{
    padding:7px;
    box-shadow:0 1px 4px rgba(0,0,0,0.2)}
#con-ar+.result-op{
    position:relative;
    top:-40px}
#con-ar:empty{
    display:none}
#content_left,#page{
    padding-left:0px!important}
#content_left>.result-op,#page>*{
    position:relative;
    left:135px}
#content_left>:not(.result-op){
    margin-left:135px!important}
.op_zhixincartoon_left,[id^="zxl_"],[class^="op_zx"]{
    max-width:539px;
    width:auto!important}
.op_zx_tieba_list td{
    padding-right:5px!important}
.op_zx_tieba_item_rate{
    white-space:nowrap}
.op_zx_renwu_music_list{
    width:100%!important}
.op_zx_renwu_music_list th[colspan="3"]+th{
    min-width:100px}
.op_zx_renwu_video_list,.op_zx_renwu_photo_list{
    overflow:auto!important}
.op_zx_renwu_video_list_item{
    width:140px!important;
    min-height:145px}
.op_zx_renwu_photo{
    padding-right:0px!important}
.op_zx_renwu_photo_list>a{
    margin-left:5px}
.op_zx_renwu_baike{
    min-height:162px}
.op_zx_renwu_baike_content,.op_zx_renwu_weibo_content_right{
    float:none!important}
.op_zx_renwu_baike_content>*:not(:last-child){
    position:relative;
    top:-7px;
    left:10px}
.op_zx_renwu_baike_content_parts{
    position:absolute;
    top:157px;
    float:left}
.op_zx_renwu_news_content_left{
    float:left;
    margin:8px 10px 0px 0px}
.op_zx_renwu_news_content_right{
    display:block}
.op-zx-mussong-headline,.op-zx-mussong,.op-zx-mussong+.c-showurl{
    width:540px!important}
.op-zx-mussong-photo{
    top:3px!important;
    left:3px!important;
    z-index:1}
.op-zx-mussong-photo>img{
    width:82px!important;
    height:82px!important}
.op-zx-mussong-photo>span{
    width:81px!important;
    height:81px!important}
.op-zx-mussong-photo:hover>img,.op-zx-mussong-photo:hover>span{
    transition:width 300ms ease-in-out,height 300ms ease-in-out}
.op-zx-mussong-photo:not(:hover)>img,.op-zx-mussong-photo:not(:hover)>span{
    transition:width 100ms ease-in-out,height 100ms ease-in-out}
.op-zx-mussong-photo:hover>img{
    width:135px!important;
    height:135px!important}
.op-zx-mussong-photo:hover>span{
    width:133px!important;
    height:133px!important}
.op-zx-mussong-box.c-clearfix{
    padding-left:95px!important}
.games-category-panel,.games-querytag{
    padding-left:3px!important}
:not(.games-search-yeyou)>.games-type{
    overflow:visible!important}
.games-type-list,.games-subject-list{
    float:none!important;
    width:auto!important;
    padding-left:0px!important}
.games-type-list a,.games-subject-list a{
    padding:0px!important;
    margin:0px!important}
.games-ordertype-shouyou{
    margin-left:413px!important}
.games-list-shouyou,.games-list-xiaoyouxi{
    padding-left:7px!important}
.games-list-item-xiaoyouxi{
    max-width:176px}
.games-subject-more.games-more-subject{
    position:absolute;
    right:22px;
    top:117px}
.games-search-yeyou .games-subject-list>br+a{
    margin-left:54px!important}
.op_zx_renwu_weibo_content_left{
    margin:7px 5px 0px 0px}
#page>*{
    font-family:arial,sans-serif!important}
#page>*:not(.n){
    background-color:transparent!important;
    opacity:1}
.fk{
    background-color:transparent}
#page .pc,.n{
    border-color:transparent!important;
    background-color:transparent!important}
.n{
    color:rgb(60,60,60)!important;
    border-radius:2px;
    background-color:rgb(237,237,237)!important;
    background-image:-moz-linear-gradient(center top,rgb(237,237,237),rgb(231,228,220))!important;
    background-image:-webkit-linear-gradient(top,rgb(237,237,237),rgb(231,228,220))!important;
    transition:background-color 500ms ease-in-out,color 500ms ease-in-out,border-color 500ms ease-in-out}
.n:hover,.n:focus,.n:active{
    background-color:rgb(213,215,215)!important;
    background-image:-moz-linear-gradient(center top,rgb(237,237,237),rgb(213,215,215))!important;
    background-image:-webkit-linear-gradient(top,rgb(237,237,237),rgb(213,215,215))!important;
    color:rgb(0,0,0)!important}
.n:active{
    border-color:rgb(231, 236, 240)!important}
#page>strong i{
    -webkit-transform:rotateY(0deg);
    transform:rotateY(0deg)}
#page>strong:hover i,#page>a:not(.n):hover~strong i{
    -webkit-animation:bp 1s ease-in-out 0s infinite alternate;
    animation:bp 1s ease-in-out 0s infinite alternate}
#page>a:nth-child(odd):not(.n):hover>:first-child,#page>a:nth-child(odd):not(.n):hover~a:nth-child(odd)>:first-child,#page>a:nth-child(even):not(.n):hover>:first-child,#page>a:nth-child(even):not(.n):hover~a:nth-child(even)>:first-child{
    -webkit-animation:fp 1s ease-in 0s infinite normal;
    animation:fp 1s ease-in 0s infinite normal}
#page>a:nth-child(odd):not(.n):hover~a:nth-child(even)>:first-child,#page>a:nth-child(even):not(.n):hover~a:nth-child(odd)>:first-child{
    -webkit-animation:fp 1s ease-out 0s infinite reverse;
    animation:fp 1s ease-out 0s infinite reverse}
@-webkit-keyframes bp{
    from{
        -webkit-transform:rotateY(0deg)}
    to{
        -webkit-transform:rotateY(360deg)}
}
@keyframes bp{
    from{
        transform:rotateY(0deg)}
    to{
        transform:rotateY(360deg)}
}
@-webkit-keyframes fp{
    from{
        opacity:1}
    to{
        opacity:0.6}
}
@keyframes fp{
    from{
        opacity:1}
    to{
        opacity:0.6}
}
#page>a>.pc:hover{
    text-decoration:underline!important}
.to_tieba{
    display:inline-block;
    position:relative;
    left:110px}
.nums{
    display:block!important;
    float:left;
    position:absolute!important;
    margin-left:0px!important;
    left:155px!important;
    margin-top:-12px}
#rs{
    margin-left:20px!important;
    background-color:transparent!important}
#rs>table{
    position:relative;
    left:135px;
    background-color:rgb(255, 247, 237);
    padding:2px 36px 6px 8px}
#rs tr{
    display:inline-block;
    float:left;
    font-size:small!important;
    padding-right:16px}
#rs tr:last-of-type{
    margin-top:22px}
#rs tr>*:not(.tt){
    display:block;
    padding-top:2px!important;
    font-size:small!important}
.tt{
    padding:3px 0px 0px!important;
    font-size:medium!important;
    font-weight:normal!important;
    display:table-caption}
#search{
    padding-top:0px!important}
[name="f2"]>*:not(.s_help){
    display:none!important}
.s_help{
    display:block;
    position:absolute!important;
    top:auto!important;
    bottom:-45px;
    left:117px}
.s_help>a{
    font-size:small!important;
    color:rgb(119,119,119)!important}
.s_help>a:hover{
    color:rgb(51,51,51)!important}
.s_help>a:active{
    color:rgb(221,75,57)!important}
#foot{
    height:40px!important;
    line-height:40px!important;
    background-color:rgb(242,242,242)!important;
    border-top:1px solid rgb(228, 228, 228);
    font-size:small!important;
    color:transparent!important}
#foot>span{
    display:none}
#passport-login-pop{
    border-radius:2px;
    box-shadow:0px 2px 2px rgba(0, 0, 0, 0.3)}
#TANGRAM__PSP_2__title{
    border-top-left-radius:2px;
    border-top-right-radius:2px}
#TANGRAM__PSP_2__body{
    border-bottom-right-radius:2px;
    border-bottom-left-radius:2px}
#TANGRAM__PSP_2__content,#pass-phoenix-login{
    border-top-color:rgb(247,247,247)!important}
#TANGRAM__PSP_8__userName,#TANGRAM__PSP_8__password{
    border-radius:1px}
#TANGRAM__PSP_8__userName:hover,#TANGRAM__PSP_8__password:hover{
    box-shadow:0px 1px 2px rgba(0, 0, 0, 0.1) inset}
#TANGRAM__PSP_8__userName:focus,#TANGRAM__PSP_8__password:focus{
    box-shadow:0px 1px 2px rgba(0, 0, 0, 0.3) inset}
#TANGRAM__PSP_8__submit{
    border:1px solid rgb(48, 121, 237)!important;
    text-shadow:0px 1px rgba(0, 0, 0, 0.1);
    background-color:rgb(77, 144, 254)!important;
    background-image:-moz-linear-gradient(center top , rgb(77, 144, 254), rgb(71, 135, 237))!important;
    background-image:-webkit-linear-gradient(top , rgb(77, 144, 254), rgb(71, 135, 237))!important}
#TANGRAM__PSP_8__submit:hover{
    border:1px solid rgb(47, 91, 183)!important;
    text-shadow:0px 1px rgba(0, 0, 0, 0.3);
    background-color:rgb(53, 122, 232)!important;
    background-image:-moz-linear-gradient(center top , rgb(77, 144, 254), rgb(53, 122, 232))!important;
    background-image:-webkit-linear-gradient(top , rgb(77, 144, 254), rgb(53, 122, 232))!important;
    box-shadow:0px 1px 1px rgba(0, 0, 0, 0.1)}
#TANGRAM__PSP_8__submit:active{
    background-color:rgb(53, 122, 232)!important;
    background-image:-moz-linear-gradient(center top , rgb(77, 144, 254), rgb(53, 122, 232))!important;
    background-image:-webkit-linear-gradient(top , rgb(77, 144, 254), rgb(53, 122, 232))!important;
    box-shadow:0px 1px 2px rgba(0, 0, 0, 0.3) inset}
#passport-login-pop-api{
    background-color:rgb(247,247,247);
    box-shadow:0px 45px 0px rgb(247,247,247)}
/*[[transparency]]*/}
