css:
@-moz-document domain("weibo.com") {
body {
    font-family: Lantinghei SC, Microsoft Yahei, Hiragino Sans GB, Microsoft Sans Serif, WenQuanYi Micro Hei, sans-serif;
}

div.WB_info {
  /* weibo用户信息 */
   display: inline !important;
}

div.WB_text {
  /* weibo正文 */
   display: inline !important;
}

  
.WB_feed .WB_feed_type {
   padding: 10px 0 0  !important;
}

div.tips_wrapper {
    display: none !important;
}

@media screen and (min-width: 700px) {
  div.WB_left_nav {
    /* 固定左侧边栏 */
    position: fixed;
    width: 150px;
  }
}

/* 不要轻易修改输入框 */
#pl_content_homeFeed, #pl_content_atmeWeibo, #pl_content_myAtCommentList, 
#pl_content_commentList, #pl_content_postedComment, #pl_content_sendMeFeed, 
#pl_content_messageList, #pl_content_myFavoritesListNarrowPlus, 
#pl_content_myFavoritesList {
  width: 800px !important; 
}
div.W_main_c{
  overflow:visible !important;
}/* 覆盖右侧栏留下的空白 */

.layer_forward .feed_repeat .num{
  margin-top:0px !important;
}
.WB_feed .type_text {
  margin:-22px 5px 0 635px !important;
}
.WB_feed .WB_screen {
  margin:-9px 0 0 750px !important;
} 
div.feed_suggest{
  display: none !important;
}

div.hot_topic{
    display: none !important;
}

.tips_warn{
    display: none !important;
}

.title_area{
    display: none !important;
}

.popular_buss{
    display: none !important;
}

.layer_tips{
    display: none !important;
}

div.webim_list, div.WBIM_fold{
  /* 隐藏侧边的好友状态通知(本人觉得没什么用) */
  display: none !important;
}
div.global_footer{
  /* 隐藏底部的链接 */
  display: none !important;
}

div.WB_right_module{
  /* 屏蔽右上方的广告 */
  display: none !important;
}

div.W_tips{
  /* 隐藏无关字眼 */
  display: none !important;
}
div.title_area{
  /* 隐藏无关字眼 */
  display: none !important;
}

/* 新版微博首页的Main区块的背景是通过图片显示的，然后右边栏上会有一条竖线存在 */
div.W_main_a{
  /* 非常掉节操地覆盖掉右边栏背景图片的竖线 */
  /* 为浅色背景 */
  /*background-color: #FFFFFF;*/
  /* 为深色背景 */
  /*background-color: #261D3B;*/
/*  background-color: /*[[bg_color]]*/ !important; */
  /* background-color: #FAFAFA !important; */ /* Default: #FFFFFF */
  background-color: /*[[bg_color]]*/ !important; /* Default: #FFFFFF */
}

/* 将快讯从右边移到左边 */
.WBIM_news {
  position: fixed;
  left: 0%;
}

div.news_btns {
  position: relative;
  margin-left: 30px;
}

/* weibo v6 部分*/
/* 清理导航栏上的按钮 */
.gn_nav_list a[nm=game], .gn_nav_list a[nm=find] {
  display: none;
}
  
#v6_pl_content_commentlist, 
#v6_pl_content_atmeweibo, 
#v6_pl_content_likelist,
#v6_pl_content_myfavoriteslist,
#v6_pl_content_notebox,
#v6_pl_content_friendsfeed, /* 好友圈 */
#v6_pl_content_groupsfeed, /* 群微博 */
#v6_pl_content_messagegroupchatnotice /* 群通知 */ {
  width: 840px;
}

#v6_pl_content_commentlist > .WB_tab_a .fr_box, 
#v6_pl_content_atmeweibo > .WB_tab_a .fr_box,
#v6_pl_content_likelist > .WB_tab_a .fr_box,
#v6_pl_content_notebox > .WB_tab_a .fr_box{
  width: 540px; /* 840px - 300px */
}

.private_list_box .private_bubble_body .bubble_cont {
  width: 700px;
  max-width: 700px;
}

/* 把私信聊天的宽度缩短 */
.webim_fold, .webim_fold .fold_bg, .webim_fold .fold_cont {
  width: 180px !important;
}

/* 把评论稍微拉长一点，让盖住点赞的图片完整显示 */
.WB_feed_repeat .repeat_list {
  padding: 0 12px 0 70px;
}

[node-type=homefeed] {
  width: 840px;
}

[node-type=homefeed] .WB_cardwrap, 
#home_new_feed_tip, .tab_box.tab_box_a.tab_box_a_r5 {
  width: 840px;
}

[node-type=homefeed] [node-type=feed_nav] .fr_box {
  width: 440px; /* 840px - 400px */
}

/* 屏蔽阿里推 */
.PCD_piclist_b {
  display: none !important;
}
}

@-moz-document url-prefix("http://www.weibo.com/u/"), url-prefix("http://weibo.com/u/") {
/* weibo v6 部分*/
/* 右侧栏 */
#v6_pl_rightmod_hongbao, #v6_pl_rightmod_recominfo, #v6_pl_rightmod_ads35, 
#v6_trustPagelet_recom_member, #v6_pl_rightmod_noticeboard {
  display: none;
}

/* 去掉打断浏览的推荐 */
.WB_cardwrap[node-type=feed_spread] {
  display: none;
}

/* 转发微博 */
.WB_text[node-type=content] > .con.S_txt2 {
  padding-left: 20px;
}

/* 针对快讯的按钮  */
.webim_news .webim_news_bd .news_btns a {
  left: 150px;
  top: -40px !important;
}

/* 去广告任重而道远 */
#v6_pl_content_biztips, #v6_pl_ad_bottomtip, .WB_feed_type[feedtype=ad], .WB_ad_tm2015 {
  display: none !important;
}

/* 临时屏蔽亚洲新歌榜 */
#v6_TrustPagelet_Recom_MusicRank, #v6_pl_rightmod_rank {
  display: none !important;
}
}

@-moz-document url-prefix("http://account.weibo.com/"), url-prefix("http://security.weibo.com/") {
/* 针对个人信息页面的特殊设置 */
div.WB_left_nav {
  position: static;
  width: 150px;
}
}
