css:
@-moz-document domain("www.nicovideo.jp") {
div#playerTabContainer div.nicommendContentInner,
div#playerTabContainer div.nicoSpotAds,
div#outline div#superBannerAds
{
display:none!important;
}
}
@-moz-document url-prefix("http://www.nicovideo.jp/video_top") {
header.header div.notice,
header.header div.complementary,
div[class="column main"] div.personalContents section#recommend,
div[class="column main"] div.personalContents section.tagList,
div[class="column sub"] section.trendtag,
div[class="column sub"] section.information,
div[class="column sub"] div.ads
{
display:none!important;
}
}
@-moz-document url-prefix("http://www.nicovideo.jp/my/video") {
div#video div.notice
{
display:none!important;
}
}
@-moz-document domain("fc2.com") {
div#fc2_ad_box,
div#fc2_bottom_bnr
{
display:none!important;
}
}
@-moz-document domain("gamewith.jp"), domain("kamigame.jp") {
div.main-wrap,
div.wrapper_main_left
{
width:100%!important;
max-width:100%!important;
}
div.main-col-wrap,
div#main_contents
{
width:80%!important;
max-width:80%!important;
}
div.sub-col-wrap,
aside#left_side
{
width:20%!important;
max-width:20%!important;
}
div.sub-wrap,
aside#right_side
{
display:none!important;
}
}
