css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.youtube.com/watch") {
.ytp-ce-element.ytp-ce-element-show,
.iv-branding .branding-img-container img,
.ytp-big-mode .ytp-chrome-controls .ytp-fullerscreen-edu-button.ytp-button {
    visibility: hidden !important;
}

#container.ytd-masthead,
#masthead-positioner,
#yt-masthead-container {
    height: 35px !important;
    margin: 0 !important;
    padding: 0 !important;
    z-index: 9999 !important;
}

ytd-masthead,
#masthead-positioner,
#yt-masthead-container {
    background-color: #00000080 !important;
}

ytd-searchbox.ytd-masthead {
    justify-content: left !important;
    margin: 0 !important;
    opacity: 0.5 !important;
}

ytd-watch-flexy:not([fullscreen]) .title.ytd-video-primary-info-renderer,
#eow-title {
    position: fixed !important;
    top: 6px !important;
    right: 277px !important;
    font-size: 14px !important;
    color: #ffffff80 !important;
    z-index: 99999 !important;
}

ytd-app:not([masthead-hidden_]) #page-manager.ytd-app {
    margin-top: 35px !important;
}

ytd-watch-flexy[theater]:not([fullscreen]) #player-theater-container.ytd-watch-flexy {
    position: relative !important;
    width: 100% !important;
    height: calc(0.5625 * 100vw) !important;
    max-height: calc(100vh - 35px) !important;
    min-height: 480px !important;
    background: #000 !important;
}

#center.ytd-masthead {
    left: 177px !important;
    position: absolute !important;
    flex: unset !important;
    width: 400px !important;
}

.watch-stage-mode #theater-background,
#placeholder-player .player-api,
#player .player-api {
    top: -14px !important;
    height: calc(0.5625 * 100vw) !important;
    max-height: calc(100vh - 35px) !important;
    min-height: 480px !important;
}

#button.ytd-button-renderer{
    border: initial !important;
}

.ytp-chrome-bottom {
    opacity: 0.4;
}

.ytp-chrome-top .ytp-share-button-visible,
.ytp-chrome-top {
    opacity: 0.4 !important;
}

.ytp-big-mode .ytp-title-text {
    font-size: 14px !important;
    text-align: right !important;
}

.ytp-big-mode .ytp-playlist-menu-button-icon,
.ytp-big-mode .ytp-share-icon,
.ytp-big-mode .ytp-cards-button,
.ytp-chrome-top .ytp-cards-button {
    opacity: 0.4 !important;
}


}
