css:
@-moz-document domain("facebook.com")
{
svg.pzggbiyp:hover,
a:hover img.bixrwtb6,
a:hover img.s45kfl79,
a:hover img.i09qtzwb,
div:hover > div > img.datstx6m,
img.ji94ytn4:hover,
div:hover > div > div > div > div > div > div > div > video
{
    filter: none !important;
}

svg.pzggbiyp,
a img.bixrwtb6,
a img.s45kfl79,
a img.i09qtzwb,
img.datstx6m,
img.ji94ytn4,
video
{
    filter: blur(5px) opacity(20%) !important;
}

svg.pzggbiyp:not(.dummy),
a img.bixrwtb6:not(.dummy),
a img.s45kfl79:not(.dummy),
a img.i09qtzwb:not(.dummy),
img.datstx6m:not(.dummy),
img.ji94ytn4:not(.dummy),
video:not(.dummy)
{
    transition: all 1s linear !important;
    transition-property: all !important;
    transition-duration: 1s !important;
    transition-timing-function: linear !important;
}
}
