css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* .identity, .identity-refresh ,*/ .identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column, .identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .left_column, .l-header-container--refresh, .identity-refresh .controls_section li:not(.section_header):hover, .identity-refresh .post_avatar, .collection_carousel .blogs_wrapper_carousel:before, .collection_carousel .blogs_wrapper_carousel:after, .discover-controls-wrapper, .discover .blog_row:hover, #search_actions_search .elevator, #discover_actions_discover .elevator, .identity .controls_section li:not(.section_header):not(.selected_blog):hover, .rapid-recs-container .rapid-recs {
  background: transparent;
}
    
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .right_column:after, .identity-refresh .radar .radar_footer .radar_avatar:before, .discover .blog_row .info:before, .discover .blog_row:hover .info:before {
  border-color: transparent;
  background: transparent;
}

.identity-refresh .post_full .post_permalink, .identity-refresh .post_full .post_permalink:not(.no-transition--dogear) {
  border-color: #ccc;
  background: transparent;
}

.identity-refresh .post_avatar .post_avatar_link, #toaster .toast .toast_border
{
  background-color: transparent !important;
}

.dashboard_options_form, .tag_psa {
  border-color: transparent;
}

.compose-button, .compose-button:active, .compose-button:focus, .identity-refresh .search_results_container .tumblelog_mask_item {
  color: #fff;
}

.plus-follow-button, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container, .identity-refresh .tab_notice .tab_notice_value {
  color: #333;
  border-color: #333;
}

.plus-follow-button:hover, .identity-refresh .controls_section .item:hover .follow:hover, .compose-button, .compose-button:active, .compose-button:focus, .identity-refresh .search_results_container .tumblelog_mask_item, #pagination a, #pagination a:hover {
  background-color: #aaa;
  border-color: #aaa;
}

#dashboard_controls a, #dashboard_controls .control {
  color: #aaa;
  text-shadow: #333 1px 1px 1px;
}

.dashboard_options_form .dashboard_options_form_disclaimer, .dashboard_options_form .dashboard_options_form_disclaimer a, .post_full .post_avatar .queue .publish_info.time {
  color: #aaa;
}

#dashboard_controls a:hover, #dashboard_controls .control:hover, .dashboard_options_form, .tag_psa, .post_full .post_avatar .queue .publish_info.day {
  color: #ccc;
}

.identity-refresh .tab_notice {
  background-color: #ccc;
}

.compose_button {
    fill: #aaa;
}

.glass.blue, #hello_glass.blue, #glass_overlay.blue, .identity-refresh .plexi.show.color, .post-forms-glass {
  background: rgba(51,51,51,.9);
}
}
