css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.pogdesign.co.uk/cat/)
{
/** HIDE
 */
.logo span,
.comment, .explain,
.bottomtext,
.buttons,
.abot,
.sp2, .sp4,
#month_box tr:nth-child(1) .sp3,
#month_box tr:nth-child(3) .sp3,
#month_box tr:nth-child(4) .sp3,
#month_box tr:nth-child(5) .sp3,
#month_box tr:nth-child(6) .sp3,
.replace,
.month_name:nth-child(odd),
ul.features:nth-child(4)
{
  display: none !important;
}
/** GENERAL SWEEP
 */
*
{
  font: 500 10pt/1.2 "Open sans" !important;
  border: 0 !important;
  text-shadow: none !important;
  letter-spacing: normal !important;
  background-color: transparent !important;
}
h2, h3, h4, h5, h6
h2 span, h3 span, h4 span, h5 span, h6 span
{
  background: transparent !important;
  height: auto !important;
}
/** LOGO
 */
#optionss .logo
{
  background: transparent !important;
  padding: 0 !important;
  width: auto !important;
}
#optionss .logo h1,
#optionss .logo span
{
  display: none !important;
}
#optionss .logo strong
{
  float: left !important;
  display: inline !important;
  padding: 0 !important;
  margin: 0 !important;
  height: auto !important;
  line-height: inherit !important;
}
#optionss .logo strong a
{
  background: transparent !important;
  padding: 0 10px 0 4px !important;
  height: auto !important;
  line-height: inherit !important;
  letter-spacing: normal !important;
}
#optionss .logo ul
{
  padding: 0 6px !important;
  height: auto !important;
  line-height: inherit !important;
}
#optionss .logo li
{
  padding: 0 !important;
  height: auto !important;
  line-height: inherit !important;
}
#optionss .logo ul li a
{
  float:left !important;
  display: inline-block !important;
  height: auto !important;
  line-height: inherit !important;
  padding: 0 4px 0 0 !important;
  width: 2.7em !important;
  white-space: nowrap !important;
  overflow: hidden !important;
  text-overflow: ellipsis !important;
}
/** TOP NAV
 */
#optionss
{
  background: transparent !important;
  height: 10px !important;
  padding: 10px 12px 10px 8px !important;
  margin: 0 auto !important;
}
#optionss ul
{
  padding: 0 !important;
  width: auto !important;
  margin: 0 !important;
}
#optionss ul.features:nth-child(2)
{
  float: left !important;
}
#optionss ul.features:nth-child(3)
{
  float: right !important;
}
#optionss li a
{
  padding: 0 !important;
  background: none !important;
}
#optionss li a:hover
{
  background: none !important;
}
#optionss ul.features:nth-child(2) li a
{
  padding-right: 12px !important;
}
#optionss ul.features:nth-child(3) li a
{
  padding-left: 12px !important;
}
/** CAL PADDING
 */
#cal, table *
{
  border-collapse: separate !important;
  border-spacing: 0 !important;
}
#cal
{
  width: 100% !important;
  margin: 0 !important;
}
#month_box *
{
  border: 0 !important;
  padding: 0 !important;
  margin: 0 !important;
}
#month_box .day p,
#month_box .today p
{
  padding: 0 10px 8px !important;
  padding: 0 8px 8px !important;
}
#month_box .day strong,
#month_box .today strong
{
  padding: 8px 10px 7px !important;
  padding: 8px 8px 7px !important;
}
/** CAL TITLE
 */
#month_box .sp1
{
  padding-right: 6px !important;
}
#month_box .sp3
{
  text-transform: lowercase !important;
  font-variant: small-caps !important;
}
#month_box td.day,
#month_box td.day strong,
#month_box td.day .t1,
#month_box td.day .t2
{
  background: transparent !important;
}
/** TEXT ALIGNMENT makes time left of episode and one same line
 */
.info p
{
  white-space: nowrap !important;
  direction: rtl !important;
  vertical-align: baseline !important;
  width: auto !important;
}
.info a
{
  display: inline-block !important;
  vertical-align: bottom !important;
  /*min-width: 80px !important;*/
  /*max-width: 200px !important;*/
}
.info br
{
  display: none !important;
}
/** TIDY BOTTOM LINKS
 */
.month_name
{
  padding: 8px !important;
  width: auto !important;
  margin: 0 !important;
}
.month_name,
.prev-month:hover,
.next-month:hover
{
  background: transparent !important;
}
.prev-month a,
.next-month a
{
  padding: 0 !important;
}
.prev-month a span
{
  padding-right: 4px !important;
}
.next-month a span
{
  padding-left: 4px !important;
}
/** SETTINGS BOX & FORMS
 */
#settingsbox
{
  top: 20px !important;
  padding: 20px 0 !important;
}
.reveal-modal-bg
{
  background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.88) !important;
}
select, option, input
{
  box-shadow: none !important;
}
.subbut
{
  text-shadow: none !important;
   box-shadow: none !important;
}
label
{
  padding-top: 8px !important;
}
/** PROFILE - this page's layout makes no sense to me
 */
.newshead
{
  height: auto !important;
  display: inline !important;
  text-indent: 9px !important;
}
.bread
{
  height: auto !important;
}
.bread li
{
  list-style-type: square;
  text-transform: capitalize !important;
  float: none !important;
  margin-left: 28px !important;
}
.bread li a,
.bread li span
{
  display: inline !important;
}
.profile ul:nth-child(1) li:nth-child(1):after
{
  content: "- You Are?";
}
.profile ul:nth-child(2) li:nth-child(1),
.profile ul:nth-child(2) li:nth-child(2),
.profile ul:nth-child(2) li:nth-child(3)
{
  display: none !important;
}
#alleps
{
  background: transparent !important;
  text-shadow: none !important;
  box-shadow: none !important;
  margin: 8px 16px !important;
}
.prfmed
{
  box-shadow: none !important;
}
.prfimg em,
.prfimg em.u20
{
  background: none !important;
  line-height: 24px !important;
}
.prfimg span
{
  display: none !important;
}
/** STATS
 */
.chartbox a {
  padding: 0 !important;
  top: 0 !important;
  bottom: 0 !important;
  font-size: 0 !important;
}
.chartbox
{
  padding: 0 !important;
}
.chartbox span
{
  overflow: hidden !important;
  font-size: 1.4em !important;
  font-weight: 900 !important;
  text-shadow: 0 0 6px #000 !important;
  height: auto !important;
  line-height: inherit !important;
  background: transparent !important;
  padding: 0 8px 0 0 !important;
  height: 40px !important;
  line-height: 40px !important;
  width: 280px !important;
  opacity: 1 !important;
}
/*[[colour_scheme]]*/
/** The End
 */
}
