css:
@-moz-document url-prefix("http://furaffinity.net"), url-prefix("http://www.furaffinity.net"), url-prefix("https://furaffinity.net"), url-prefix("https://www.furaffinity.net") {

/*[[csr_color]]*/

body > div {
    padding-top: 45px !important;

}

.block-menu-top a { color: /*[[color1]]*/ !important;}
.block-menu-top { 
/*[[mtype]]*/
}

ul.dropdown .noblock [href*="controls"] { 
    display: none !important;
}

#my-username, ul.dropdown .noblock [title*=Notes] { 
 
    color: /*[[color1]]*/ !important;
    text-transform: capitalize;
}

#logout-link { 
    color: #fff !important;
    margin-left: -12px;
    margin-right: -5px;
}

#logout-link:hover { 
    color: #FF3333 !important;
}

a:hover {
  text-decoration: none !important;
  text-shadow: 0px 0px 5px /*[[color2]]*/ !important;
}

 body, div div, div.bbcode, td.ldot {
    background-color: #000 !important;
    color: /*[[color1]]*/ !important}
 td {
    background-color: #111 !important;
    border: none !important;
    color: /*[[color1]]*/ !important}
 div.no_overflow, div div div {
    background: transparent !important}
 div.tab a, td.cat, td.cat a, .lead, td#page-comments li, 

td.cat div.dicks, h4 {
    background-color: #000 !important}
 div.tab u {
        color: #000 !important;
    background-color: /*[[color1]]*/ !important}
 h1, h2, h3, h4, h5, h6, p {
    color: #FFF !important;
    border: none !important;
    background: transparent !important}
 input, textarea, select, button, select.selected, a.prev, 

a.next {
    background-color: #000 !important;
    color: #FFF !important;
    -moz-appearance: none !important;}
}
