css:
@-moz-document url("about:home") {
             
    #sync,
    /*[[se_button]]*/
    #apps, 
    #aboutMozilla,
    #snippetContainer,
    #searchEngineLogo,
    #restorePreviousSessionSeparator { 
    display: none !important 
    }         
    
    body,
    body[class="background-4871"] {
    background: url('/*[[back_image]]*/') /*[[back_pos]]*/, #222 !important
    }  
    
    #brandLogo {
    opacity: /*[[logo_opacity]]*/ ;
    width: 350px !important;
    padding-bottom: 140px !important;
    background: url('http://imgur.com/kvlsxBb.gif'),url('http://www.hostpic.org/images/1412270110240091.png') !important  
    }
    
    #searchForm {
    padding: 0px 5px 90px !important; 
    margin: 0px 0 -80px 0 !important; 
    max-width: 456px !important; 
    min-width: 456px !important; 
    } 
    
    #searchText {
    height: 32px !important;
    border-radius: 0px !important; 
    font: 17px Lucida Sans Unicode ; 
    text-shadow: 1px 1px 1px rgba(150,150,150, .35) !important;
    box-shadow: 0px 1px 3px rgba(0,0,0,.2) !important;
    }
    
    #searchText::-moz-placeholder { 
    font-weight: bold;
    text-shadow: none;
    color: #0E0E0E !important
    }
    
    #searchText:hover { 
    background: radial-gradient(at top,rgba(255,255,255,.5),rgba(255,255,255,.1),rgba(255,255,255,.2)) !important; 
    }    
    
    /*[[focused_textbox]]*/ { 
    background: radial-gradient(at top,rgba(255,255,255,.5),rgba(255,255,255,.5),rgba(255,255,255,.1)) !important; 
    } 
    
    #searchSubmit,
    #searchSubmit:hover, 
    #searchSubmit:focus {
    -moz-appearance: none !important;
    border-radius: 0px ; 
    padding: 0px 0px 0px ;
    color: #262626 !important;
    margin-left: 3px ;
    font: bold 13px verdana ; 
    box-shadow: 0px 1px 3px rgba(0,0,0,.2) !important; 
    text-shadow: 1px 1px 0px rgba(50,50,50,.4),0px -1px 0px rgba(200,200,200,.3)
    }   
    
    #searchSubmit:hover, 
    #searchSubmit:focus {
    color: #00BBFF !important;
    background: radial-gradient(at top,rgba(255,255,255,.6),rgba(200,200,200,.4),rgba(150,150,150,.4)) !important; 
    }
    
    ::-moz-selection       { background-color: transparent }
    #searchText::-moz-selection { background-color: orange } 
        
    #launcher { 
    background: transparent !important;
    box-shadow: none !important;
    border: none !important; 
    } 
        
    #launcher button { 
    padding: 0px 0px 26px !important;
    margin-bottom: 0px !important; 
    margin-top: 10px !important;
    font-size: 13px !important;
    opacity: 1 !important;       
    }
    
    .launchButton {
    font-family: Lucida Console;
    min-width: 75px !important;  
    max-width: 75px !important;
    min-height: 92px !important; 
    /*[[button_text]]*/   
    background: radial-gradient(at top,rgba(150,150,150,0),rgba(150,150,150,.07),rgba(150,150,150,0)) !important;
    }
        
    .launchButton:hover,
    #restorePreviousSession:hover { 
    color: #00BBFF !important;
    background: radial-gradient(at top,rgba(200,200,200,.45),rgba(200,200,200,.05),rgba(200,200,200,.2)) !important; 
    } 
        
    #restorePreviousSession::before {
    content: url("chrome://browser/content/abouthome/restore.png") !important;
    padding: 0px 9px 0px !important;
    margin-bottom: -10px !important;
    margin-top: 5px !important; 
    }
    
   /*[[unfocused_search]]*/
