css:
@-moz-document url(about:newtab)
{
/** BACKGROUND
 */
#newtab-scrollbox
{
  background: /*[[ybgcol]]*/ !important;
  background-image: url(/*[[ywallp]]*/) !important;
  background-position: top center !important;
  background-size: /*[[ybgsize]]*/ !important;
}
/** GRID BORDERS
 */
.newtab-row
{
  border-bottom: /*[[ygridw]]*/px solid rgba(/*[[ygridc]]*/,/*[[ygrido]]*/) !important;
}
.newtab-cell
{
  border-right: /*[[ygridw]]*/px solid rgba(/*[[ygridc]]*/,/*[[ygrido]]*/) !important;
}
/** CONFIG
 */
#options-toggle {
  height: 18px !important;
  width: 18px !important;
  background-repeat: no-repeat !important;
  background-position: -99px 0 !important;
}
#options-toggle:hover {
  background-position: 0 0 !important;
}
/** TITLE
 */
.newtab-title
{
  display: none !important;
  top: 0 !important;
  bottom: auto !important;
  background: transparent !important;
  background-image: linear-gradient(to top, rgba(255, 255, 255, 0) 0%, rgba(196, 196, 196, 0.2) 100%) !important;
  padding: 3px 6px !important;
  height: calc(100% - 5px) !important;
}
.newtab-link:hover .newtab-title
{
  padding: 1px 4px !important;
}
/** THUMBNAILS
 */
.newtab-thumbnail
{
  background-image: none !important;
  background-color: transparent !important;
  background-repeat: no-repeat !important;
  background-position: center center !important;
  background-size: auto !important;
  opacity: /*[[ylogot]]*/ !important;
}
.newtab-thumbnail:hover
{
  opacity: /*[[ylogoh]]*/ !important;
}
/** LINK HOVER
 */
.newtab-link:hover
{
  background: /*[[ylinkh]]*/ !important;
}
/** TRANSITION
 */
/*[[yspeed]]*/

/** LOGOS
 */
/*[[ylogos]]*/
/** GRID BORDER+PADDING
 */
*
{
  border-radius: 0 !important;
  box-shadow: none !important;
  outline: none !important;
}
/*[[ygridp]]*/
.newtab-row
{
  margin: 0 !important;
}
.newtab-cell
{
  background-color: transparent !important;
  border-top: 0 !important;
  border-bottom: 0 !important;
  border-left: 0 !important;
  margin: 0 !important;
}
.newtab-cell:last-child
{
  margin: 0 !important;
}
/** GRID ROW+CELL PADDING
 */
.newtab-row:last-child,
.newtab-cell:last-child
{
  border: 0 !important;
}
/** UNDO BOX
 */
#newtab-undo-container
{
  display: none !important;
  color: #000 !important;
}
#newtab-undo-container
{
  background: rgba(255,255,255,1.0) !important;
  position: absolute !important;
  top: 8px !important;
  left: 40% !important;
}
/** CONTROL ICONS
 */
.newtab-control
{
  background-image: none !important;
  height: 22px !important;
  width: 24px !important;
}
.newtab-control:hover
{
  background-image: url(https://i.imgur.com/wdJ9wob.png) !important;
}
.newtab-control-pin
{
  top: -moz-calc(100% - 25px) !important;
  left: 2px !important;
}
.newtab-control-block
{
  top: -moz-calc(100% - 25px) !important;
  right: 2px !important;
}
#newtab-toggle
{
  opacity: 0 !important;
  top: 4px !important;
  right: 4px !important;
  width: 9px !important;
  height: 9px !important;
  background: url(https://i.imgur.com/3hDVAFa.png) -224px 1px !important;
}
#newtab-toggle:hover
{
  opacity: 1 !important;
}
#newtab-toggle[page-disabled="true"]
{
  background-position: -240px 1px !important;
}
/** THE END
 */
}
